INSERT INTO `diary_as` (`id`, `category`, `diary_date`, `diary_dt`, `diary_note`, `sort_no`, `delete_flg`, `update_user`, `ip_addr`, `created`, `modified`) VALUES
(1, 'diary', '2010-01-13', '2010-01-13 00:00:00', '一日中休みであったが、何をするでもなく無駄に一日を過ごす。', 0, 0, 'manual', NULL, '2010-01-13 00:00:00', '2017-02-03 14:14:45'),
(2, 'diary', '2010-01-14', '2010-01-14 00:00:00', '午前中にUML資格の勉強を行う。午後眠る。集会、そして仕事。休みの間、だらだら過ごしたせいで非常に気持ちが沈む。休みの日には計画を立て何かをするようにしなければならないと実感する。', 0, 0, 'manual', NULL, '2010-01-14 00:00:00', '2017-02-03 14:14:45'),
(3, 'diary', '2010-01-22', '2010-01-22 00:00:00', '仕事疲れのため1日中寝る。バイトでの仕事のことではあるが、いつもたむろしてる連中を店長が追い出した模様。だいぶたすかる。', 0, 0, 'manual', NULL, '2010-01-22 00:00:00', '2017-02-03 14:14:45'),
(4, 'diary', '2010-01-23', '2010-01-23 00:00:00', 'ひさしぶりに部屋掃除、水換えなどを行う。夜から仕事。', 0, 0, 'manual', NULL, '2010-01-23 00:00:00', '2017-02-03 14:14:45'),
(5, 'diary', '2010-01-24', '2010-01-24 00:00:00', '朝までバイト。バイトは特に異常なし。午前は集会、午後に２つの奉仕を司会する。奉仕司会においては半なき状態になるほど、やる気がおこらなかったが、祈りのうちに何とかこなすことができた。8時くらいに寝る。つぼをいろいろ押すと体の疲れを軽減できる。', 0, 0, 'manual', NULL, '2010-01-24 00:00:00', '2017-02-03 14:14:45'),
(6, 'diary', '2010-01-25', '2010-01-25 00:00:00', '部屋の掃除や洗濯をゲームをしながら行うが非常に時間がかかった。平行作業はゲームよりにならないよう注意せねばならない。今日の夜は仕事。', 0, 0, 'manual', NULL, '2010-01-25 00:00:00', '2017-02-03 14:14:45'),
(7, 'diary', '2010-01-26', '2010-01-26 00:00:00', '夜勤明け。掃除洗濯、割当練習、資格勉強などを行う。日々の聖句等ができなかったのが反省。その他は比較的に良い', 0, 0, 'manual', NULL, '2010-01-26 00:00:00', '2017-02-03 14:14:45'),
(8, 'diary', '2010-01-27', '2010-01-27 00:00:00', '掃除洗濯、割当練習、資格勉強、個人研究等を行う。良い一日だった。仕事も８時までであったが、よく飯を食べたせいか、精力的に働くことができた。', 0, 0, 'manual', NULL, '2010-01-27 00:00:00', '2017-02-03 14:14:45'),
(9, 'diary', '2010-01-28', '2010-01-28 00:00:00', '夜勤明け。さすがに疲れた。午後におきるがゲームをしすぎたので反省。今日の集会の割当はどちらかといえば失敗。第一会場の声が気になり練習成果がでなかった。もっと声をだす必要がある。夜は資格勉強と個人研究を行う。', 0, 0, 'manual', NULL, '2010-01-28 00:00:00', '2017-02-03 14:14:45'),
(10, 'diary', '2010-01-30', '2010-01-30 00:00:00', '早朝、予習をする。夜勤明け。家事はこなすが日々の聖句や資格勉強をしなかったのは良くなかった。夕方から本部へ行く。', 0, 0, 'manual', NULL, '2010-01-30 00:00:00', '2017-02-03 14:14:45'),
(11, 'diary', '2010-01-31', '2010-01-31 00:00:00', '午前中は本部会衆で交わる。正午頃、嘉数兄弟のＰＣ設定をする。お礼に3000円ももらう。たいへんありがたい。午後は漫画を読んだ。しかしこの漫画は暴力的であったため読むべきでも買うべきでもなかった。反省せねばならない。夕方になって3人（父母自）で八重桜を見に行く。霧桜だった。晩はおでんを食って、啓の労働をひやかしたあと１０時くらいに寝る。', 0, 0, 'manual', NULL, '2010-01-31 00:00:00', '2017-02-03 14:14:45'),
(12, 'diary', '2010-02-01', '2010-02-01 00:00:00', '遅くまで寝てしまった。午前はゲームをしすぎてしまった。帰り支度をした後、北谷へ帰還。米や野菜をもらう。帰りにマンガ倉庫へより、例のマンガを売り飛ばす。帰ってからは家事、日々の聖句等を行う。水槽の新インテリアの準備のため砂を採取し洗浄。今晩は仕事。', 0, 0, 'manual', NULL, '2010-02-01 00:00:00', '2017-02-03 14:14:45'),
(13, 'diary', '2010-02-02', '2010-02-02 00:00:00', '夜勤を終え、午前中に起きる。午後は家事、各種勉強、集会準備を行う。海水魚水槽をリセットし、以後水槽Ｃと呼ぶ。まだ何を飼うかは決めていない。晩は集会。巡回訪問。日々の聖句や聖書通読を毎日行うことを学んだが、すでにやっていることなのでより励みになった。奉仕においての目標は質問を用いて会話をすること。自分にそれができるだろうか？　問題は集会後の深夜。インターネットを夜遅くまで世の情報を見すぎてしまった。例のサイトは面白く有益な情報もあるが、悪い情報も大量にあるため絶対に見てはならない。時間がひどくとられる。', 0, 0, 'manual', NULL, '2010-02-02 00:00:00', '2017-02-03 14:14:45'),
(14, 'diary', '2010-02-04', '2010-02-04 00:00:00', '集会出席し、巡回監督の話を聞く。愛の自然に広まる性質を利用して愛を広げることを学ぶ。夜仕事', 0, 0, 'manual', NULL, '2010-02-04 00:00:00', '2017-02-03 14:14:45'),
(15, 'diary', '2010-02-05', '2010-02-05 00:00:00', '昼間は疲れて遅くまで寝ていたかと。家事などを行うが個人研究や四角勉強はできなかった。', 0, 0, 'manual', NULL, '2010-02-05 00:00:00', '2017-02-03 14:14:45'),
(16, 'diary', '2010-02-06', '2010-02-06 00:00:00', '先日夜遅くまで起きてしまったせいであまり眠れず。週末の非常にハードなスケジュールにより、かなり気持ちが落ち着かず。非常に苦悩する。', 0, 0, 'manual', NULL, '2010-02-06 00:00:00', '2017-02-03 14:14:45'),
(17, 'diary', '2010-02-07', '2010-02-07 00:00:00', '週末＋８時間＋午後の活動＋連続仕事のプレッシャーなどもあり、仕事で極度に疲労し、精神的に折れる。肉体的にも精神的にも極度の疲労。集会へ出席できず、まる１日寝る。しかしそれでも体力も精神も回復できず。', 0, 0, 'manual', NULL, '2010-02-07 00:00:00', '2017-02-03 14:14:45'),
(18, 'diary', '2010-02-08', '2010-02-08 00:00:00', '昨日とはうってかわり仕事は楽だったが、疲れが残っていることもあり、家に帰ってもろくなことができず。', 0, 0, 'manual', NULL, '2010-02-08 00:00:00', '2017-02-03 14:14:45'),
(19, 'diary', '2010-02-09', '2010-02-09 00:00:00', '残っている疲れと昨日遅くまでおきていたこともあり、午後２時におきてしまうしまつ。銀行へ行く予定が中止。昨晩のインターネットのやりすぎに反省。晩は資格勉強を行うがそれ以外はできなかった。', 0, 0, 'manual', NULL, '2010-02-09 00:00:00', '2017-02-03 14:14:45'),
(20, 'diary', '2010-02-10', '2010-02-10 00:00:00', '朝早く起きるがおもしろいＷＥＢ漫画を読みすぎてしまう。午前中を無駄に過ごしてしまった。銀行口座を作りにいくが地元でないと作れないとの事。午後２時に疲れのため寝て午後１０時に起きる。やるべきことがまったくできていない反省すべき一日であった。', 0, 0, 'manual', NULL, '2010-02-10 00:00:00', '2017-02-03 14:14:45'),
(21, 'diary', '2010-02-11', '2010-02-11 00:00:00', '夜勤明け。メカリ兄弟が昨晩から泊まっている。疲れを感じているためすぐに寝る。集会では朗読の勤めがあった。また夜遅くまで起きてしまう。反省せねばならない。寝れないときは勉強を行うようにしよう。', 0, 0, 'manual', NULL, '2010-02-11 00:00:00', '2017-02-03 14:14:45'),
(22, 'diary', '2010-02-12', '2010-02-12 00:00:00', '午前は車のオイル交換を行う。ブレーキオイルやステアリング交換、オイルタンク洗浄も行ったため代金は１２０００円に。痛い出費ではあるが、車は好調になったので良いかと思う。　その後そのまま　本部へ行き、沖縄銀行で口座を開設する。帰ってからは家事を行う。家事を行うとすでに寝る時間になったため夕方からは夜勤に備えて寝る。仕事出勤前の現在、体力は回復しているため、明日は奉仕へ行きたい。帰ったら早めに寝よう。', 0, 0, 'manual', NULL, '2010-02-12 00:00:00', '2017-02-03 14:14:45'),
(23, 'diary', '2010-02-13', '2010-02-13 00:00:00', '夜勤明け。結構疲れた。午前中にすぐ眠らず、だらだらと過ごしすぎた。おかげで寝るのが１１時くらいになり、また奉仕へ行く気力もなくした。午前中には奉仕の準備と祈りにより奉仕へ行く気力を充実させておくべきであった。\r\n１９時くらいに起きご飯を食べる。２２時くらいまで気力が沈んだ状態で何もできない。２２時以降はゲームしながら家事などを行うと気力を回復してきた。朗読練習を他のことをしながら４時間ぐらい行った。夜遅くまでおきるとしても霊的な事柄であれば有益だと感じる。明日の集会が終わったら奉仕の準備などを行い気力を充実させたいところだが、部門の集まりを優先させねばならないだろう。2009/12/15ものみの塔第２研究記事は自分にかなりあてはまるのでこれからも重宝したい。生活に喜びを見出すことについて書かれている。', 0, 0, 'manual', NULL, '2010-02-13 00:00:00', '2017-02-03 14:14:45'),
(24, 'diary', '2010-02-14', '2010-02-14 00:00:00', '午前は集会。ものみの塔の朗読をこなす。良い結果だった。ものみの塔からかなりの益を得た。午後は寝る。夕方に起きて運送部門の集まりに出席する。帰ってから少し寝て仕事へ行く。ものみの塔の研究記事には自分ができることの限界をわきまえることにより思い煩わないようにすることを学んだ。大会、部門の仕事、来週の奉仕司会などの時間調整で思い煩っていたが、一応時間調整願いを出してエホバに祈ることにより思い煩わずに済んだ。エホバは祈りを聞かれ時間調整が受理された。とてもありがたい。　あと仕事前に白米を大量に食べると、仕事を精力的に行うことができることを知る。ラーメンは厳禁。', 0, 0, 'manual', NULL, '2010-02-14 00:00:00', '2017-02-03 14:14:45'),
(25, 'diary', '2010-02-15', '2010-02-15 00:00:00', '夜勤明け。午前中に家事等を行う。午後は疲れてずっと寝る。夜は遅くまで動画を見てしまった。なすべきことができなかった。', 0, 0, 'manual', NULL, '2010-02-15 00:00:00', '2017-02-03 14:14:45'),
(26, 'diary', '2010-02-16', '2010-02-16 00:00:00', '９時くらいに起き家事等を行う。奉仕へ行くつもりであったが、まったくやる気が起こらず無気力状態に陥ってしまう。無気力状態への対策方法を練る必要がある。正午は無気力状態のためゲームやダラダラと過ごし気づけば夕方に。夕方から資格勉強を始めるが眠いので寝る。夜１１時くらいに起き資格勉強をたくさん行う。', 0, 0, 'manual', NULL, '2010-02-16 00:00:00', '2017-02-03 14:14:45'),
(27, 'diary', '2010-02-17', '2010-02-17 00:00:00', '朝４時くらいまで個人研究（無気力について）や熱帯魚関連のサイトをみて過ごす。しかし今回、信じられない事がおこる。朝４時に眠り午前９時くらいに起きるだろうと思い込んでいたのが、午前９時でなく午後１０時くらい。どうやら体内時計が激しく狂ってしまったようだ。必ずアラームを付けて寝るようにせねばならない。しかし17時間の眠りっぱなしとは・・・。', 0, 0, 'manual', NULL, '2010-02-17 00:00:00', '2017-02-03 14:14:45'),
(28, 'diary', '2010-02-18', '2010-02-18 00:00:00', '夜勤明け。１１時まで無駄に時間を過ごしてしまう。冷え込みか体内時計の狂いが原因か知らないが、関節が非常に痛む。夕方まで寝ることにしたが、関節痛がひどいため集会直前まで寝る。一応足の痛みはとれたが、集会の準備は一切できなかった。集会へいけただけましだろうか。徹夜でおきてしまい、ゲームやインターネットでかなり時間を無駄に過ごしてしまう。非常に反省すべきである。体調不良などのストレスが原因かもしれないので、短時間でストレス発散するようにすべきだろう。', 0, 0, 'manual', NULL, '2010-02-18 00:00:00', '2017-02-03 14:14:45'),
(29, 'diary', '2010-02-19', '2010-02-19 00:00:00', '朝１０時くらいに起床し、家事等を行う。しかし体調がひどく悪くあまり動けない。食欲すらないほど。ジャガイモを食べて午後１時くらいには寝る。夜１０時に起きると体調は回復。日々の聖句、聖書通読、日誌を書く。出勤。　体調不良の原因は徹夜と暴食であろうと思われる。休みの日は夜は寝るようにし、食べる量も控えめにすべきだ。また体調不良で仕方がなかったとしても寝すぎには気をつけよう。', 0, 0, 'manual', NULL, '2010-02-19 00:00:00', '2017-02-03 14:14:45'),
(30, 'diary', '2010-02-20', '2010-02-20 00:00:00', '夜勤明け。熱と激しい頭痛に際悩まされる。関節もやや痛む感じがありインフルエンザの疑いもある。飲み物を大量に飲みながら午前中は１１時までおきる。その後療養。奉仕へ行く予定であったのだが・・・。', 0, 0, 'manual', NULL, '2010-02-20 00:00:00', '2017-02-03 14:14:45'),
(31, 'diary', '2010-02-21', '2010-02-21 00:00:00', '念のため集会を休む。正直なところ集会へ行けたかもしれないが、午後の奉仕司会のためひどく憂鬱な気分になってしまう。体調が万全でないのも重なってしまい･･･。今日一日は何もできず、インターネットを見たり寝たりするのみ。非常に最悪な一日。おまけに夜からは仕事。', 0, 0, 'manual', NULL, '2010-02-21 00:00:00', '2017-02-03 14:14:45'),
(32, 'diary', '2010-02-22', '2010-02-22 00:00:00', '夜勤明け。家事を行うが基本的にほかの事は何もできず。土曜日から調子の悪さをひきづった状態。このままでは奉仕報告が今月０時間になってしまうのを杞憂に思う。必要以上に自分を責めぬようにせねばならない。奉仕練習を毎日行い、奉仕に対する熱意を保たねばならない。', 0, 0, 'manual', NULL, '2010-02-22 00:00:00', '2017-02-03 14:14:45'),
(33, 'diary', '2010-02-23', '2010-02-23 00:00:00', '奉仕時間については真にメールでの証言を行うことにする。午前中は家事を行う。午後は寝て夜から仕事。', 0, 0, 'manual', NULL, '2010-02-23 00:00:00', '2017-02-03 14:14:45'),
(34, 'diary', '2010-02-24', '2010-02-24 00:00:00', '夜勤明け。午前中を無駄に時間を浪費する。仕事が終わり浮かれすぎたのが原因。午後くらいに寝て、夜からまた仕事。仕事前に真からのメールを確認。集会へは行く気があるようなのでとりあえず安心した。一応奉仕報告も１時間は出せた。', 0, 0, 'manual', NULL, '2010-02-24 00:00:00', '2017-02-03 14:14:45'),
(35, 'diary', '2010-02-25', '2010-02-25 00:00:00', '夜勤明け。午前中は飯を食べながら家事などやるべきことを行う。無駄に過ごした部分もあったので反省する。午後２時くらいに寝る。夜仕事', 0, 0, 'manual', NULL, '2010-02-25 00:00:00', '2017-02-03 14:14:45'),
(36, 'diary', '2010-02-26', '2010-02-26 00:00:00', '夜勤明け。朝、運送部門での作業を行う。その後、しばらく起きているが、疲れで、満腹なのか空腹なのか分からなくなり、暴食してしまう。正午から４時くらいまで寝る。その後、７時すぎまで、家事などやるべきことを行う。１０時まで無駄に時間を過ごしねる。\r\n夜勤明けなど疲れているときには眠くなくても直ぐ寝る必要がある。また時間の無駄を避ける。何かをしながらという癖をつける。', 0, 0, 'manual', NULL, '2010-02-26 00:00:00', '2017-02-03 14:14:45'),
(37, 'diary', '2010-02-27', '2010-02-27 00:00:00', '朝５時くらいにおきてまもなく、大きな地震が起こる。最大震度５弱、M6.9、沖縄本島近隣海域（太平洋側）。体感震度は４くらいあったとおもうが、特に被害はなし。\r\n日中は巡回大会。主に霊性について学ぶ。帰ってから腹の調子が悪い。先日の暴食が原因かと。地震の影響かお湯蛇口から水が出ない。どうしたものか。\r\n馬超、啓、遠藤姉妹が泊まる。', 0, 0, 'manual', NULL, '2010-02-27 00:00:00', '2017-02-03 14:14:45'),
(38, 'diary', '2010-02-28', '2010-02-28 00:00:00', '朝４時半に起きて、家事、勉強などやるべきことをいろいろ行う良い時間を過ごす。\r\n大会２日目。霊性を守るために、娯楽に気をつけ、霊的な食物を定期的にとるようにすることを協調された。\r\n昨日チリ地震M８．８の影響で大津波警報（２ｍクラス）あり。午後３時くらいに到達予定。被害は見られず。\r\n大会終了後、糸満市にある旧西崎会衆まで荷物を運ぶ。晩はインターネットを見ながらゆっくり時間を過ごし、１１頃に寝る。', 0, 0, 'manual', NULL, '2010-02-28 00:00:00', '2017-02-03 14:14:45'),
(39, 'diary', '2010-03-01', '2010-03-01 00:00:00', '朝７時過ぎに起きるが、午前中は時間を無駄に過ごしてしまう。午後は午後４時までやるべきことを行う。本日、夜勤。津波の被害はほぼ皆無。50cmくらいの波が川を逆流したくらい。', 0, 0, 'manual', NULL, '2010-03-01 00:00:00', '2017-02-03 14:14:45'),
(40, 'diary', '2010-03-02', '2010-03-02 00:00:00', '夜勤明け。仕事に帰って３時間くらい無駄にする。１時間くらいにすればよかった。２時半まで寝て、やるべきことを行い有益に次官を過ごす。夜に本部に行く', 0, 0, 'manual', NULL, '2010-03-02 00:00:00', '2017-02-03 14:14:45'),
(41, 'diary', '2010-03-03', '2010-03-03 00:00:00', '本部に滞在中。午前はレンのダニ取りを行い、正午前後に少し昼寝、午後は海洋博記念公園を散歩する。海沿いから垣の内に出て帰る。夜は焼肉をごちそうになり、夜遅く、北谷へ戻る。特にやるべきことを行っていないが聖書通読くらいはしておけば良かった。髪を切ってもらう予定だが、レンのダニ取りのためきってもらえなかった。', 0, 0, 'manual', NULL, '2010-03-03 00:00:00', '2017-02-03 14:14:45'),
(42, 'diary', '2010-03-04', '2010-03-04 00:00:00', '午前は成すべきことするが、午後からインターネット上の読み物に夢中になり、時間を無駄に過ごす。寝る時間も短めになる。結局予習もできずじまい。夜は集会。集会で北谷会衆と山里会衆に分かれる発表あり。深夜から仕事。', 0, 0, 'manual', NULL, '2010-03-04 00:00:00', '2017-02-03 14:14:45'),
(43, 'diary', '2010-03-05', '2010-03-05 00:00:00', '夜勤明け。午前中にやるべきことを少し行う。午後３時くらいまで寝る。起きてまたやるべきことを行い早い時間に寝る。日々の聖句などができなかったが、比較的、良い一日を過ごせたかも', 0, 0, 'manual', NULL, '2010-03-05 00:00:00', '2017-02-03 14:14:45'),
(44, 'diary', '2010-03-06', '2010-03-06 00:00:00', '午前中は家事などやるべきことを行う。午前は奉仕へ行こうと思ったが、晩からの集会と今日は仕事がかなりたいへんになりそうだったため、見合わせる。午後２時くらいまで、交通事故に関する動画と、自殺問題についてインターネットで調べる。午前１１時くらいには寝ておけばよかった。晩は集会。注解はできたが討議に集中できないという問題あり。晩から仕事。那覇マラソンのせいで客多し。結構仕事を手抜きしたおかげでなんとか過度の疲れを免れる。', 0, 0, 'manual', NULL, '2010-03-06 00:00:00', '2017-02-03 14:14:45'),
(45, 'diary', '2010-03-07', '2010-03-07 00:00:00', '夜勤明け。今回は結構疲れた。帰って直ぐ寝る。午後４時くらいまで寝る予定であったが、午後１時半くらいにおきる。奉仕練習を重点にやるべきことを行う。聖書通読で祈りもとめ続けることについて学んだことが、奉仕へ行くための勇気に役に立った。５時半より奉仕。司会であったがとてもスムーズにできた。エホバが祈りを聞かれたのかもしれない。晩は裕介のみやげのカニのから揚げを食べつつ、やるべきことを行う。後半、ネットで夜更かししそうになったが、なんとか１２時くらいにねることができた。自己制御ができたのは日々の良い生活の賜物だろう。本日はとても良い一日だった。', 0, 0, 'manual', NULL, '2010-03-07 00:00:00', '2017-02-03 14:14:45'),
(46, 'diary', '2010-03-08', '2010-03-08 00:00:00', '朝７時くらいにおきるつもりであったが、朝６時半に起きる。良い生活の成果が出ているのかもしれない。午後１時くらいまでやるべきことを行う。目標すべて達成。午後１時くらいには寝る。本日、夜勤。火曜日なので客も多く結構疲れた。', 0, 0, 'manual', NULL, '2010-03-08 00:00:00', '2017-02-03 14:14:45'),
(47, 'diary', '2010-03-09', '2010-03-09 00:00:00', '夜勤明け。裕介が朝から文句を言ったため気がめいり、午前中はやけくそ状態になり、ろくなことができず。他の人に失意を感じたときは忘れるようにつとめ、やるべきことをひたすら行うのが良い。疲れていたためイラつきやすい状態になっていた。午後２時に起きTODO（やるべきこと）をすべてこなす。午後１１過ぎに寝る。とてもすばらしい。祈りの時間を取り分けるようにしたい。残金残りわずかである。晩には午前中のいらつきは忘れる。', 0, 0, 'manual', NULL, '2010-03-09 00:00:00', '2017-02-03 14:14:45'),
(48, 'diary', '2010-03-10', '2010-03-10 00:00:00', '朝、７時くらいにおき、TODOを行う。本日奉仕に出席する予定であったが、場所が分からず出席できず。若干気落ちもする。\r\n１１時くらいに太田兄弟来て、WORDを見てほしいとのことだったので直す。また金城清兄弟から記念式招待ビラの印刷をたのまれる。この印刷ははじめは上手くいったが後半から紙づまりやインクの莫大な消費により、半分は印刷できなかった。残り半分はナオキにお願いすることになった。インク代で2000円消し飛ぶ。晩の奉仕も上記の理由により忙しくて果たせなかった。熱意が低下してしまったのもある。給料の振込を確認するが思ったより少なく、愕然とし、気落ちする。しかし、何気なくメールを開いてみると１０前くらいにWEB製作の仕事の依頼があったので返信する。初めての請負仕事なのでとても不安だ。', 0, 0, 'manual', NULL, '2010-03-10 00:00:00', '2017-02-03 14:14:45'),
(49, 'diary', '2010-03-11', '2010-03-11 00:00:00', '朝起きると、仕事のメールの返信が来た。早速見積もりを出して返信してみる。この作業に時間を多くとられたので、淡々と仕事を迅速にこなす必要がある。', 0, 0, 'manual', NULL, '2010-03-11 00:00:00', '2017-02-03 14:14:45'),
(50, 'diary', '2010-03-12', '2010-03-12 00:00:00', '夜勤明け。朝に家事を少し行い、少し寝る。そして銀行と買い物に行く。午後から先日受けた仕事の依頼をするが夜まで分析してみた結果、断ることに。最初から断っておけばよかった。やる気を消失し、寝る。ほかのことができず。', 0, 0, 'manual', NULL, '2010-03-12 00:00:00', '2017-02-03 14:14:45'),
(51, 'diary', '2010-03-13', '2010-03-13 00:00:00', 'やる気が消沈中。８時に起き、TODOをこなしつつ午後１くらいにおきる。午後６時前にはおきる。その後TODOを。精神的に疲れ気味。', 0, 0, 'manual', NULL, '2010-03-13 00:00:00', '2017-02-03 14:14:45'),
(52, 'diary', '2010-03-14', '2010-03-14 00:00:00', '仕事を８時に終え、少し休んですぐ集会。やはり集中力にかけてしまう。注解を準備したのに白昼夢状態のため逃してしまう。白昼夢をなんとかしなければならない。奉仕は休み、集会を終えてすぐ寝る。４時ごろ起き、ネットを見て６時に寝る。寝つきが悪い。しかし、エホバに祈ることにより、仕事をスムーズにこなすことができた。', 0, 0, 'manual', NULL, '2010-03-14 00:00:00', '2017-02-03 14:14:45'),
(53, 'diary', '2010-03-15', '2010-03-15 00:00:00', '夜勤明け。仕事の心得。奮起と休憩、ゆっくり動く。そして集中。午前中に家事を中心にTODOをこなす。午後3時くらいまでネットで漫画を読んでしまう。寝る時間が遅れてしまい、仕事に行くためにおきるのがたいへんだった。娯楽は時間を厳守して追えるようにせねばならない。', 0, 0, 'manual', NULL, '2010-03-15 00:00:00', '2017-02-03 14:14:45'),
(54, 'diary', '2010-03-16', '2010-03-16 00:00:00', 'ハードな3日間が終わり、気が抜けてしまい、ほぼ1日中非常によくない時間を過ごしてしまう。労苦を終えた後の反動を考慮しなかったのが原因。たいへんなときを終えたときこそ気を引き締めなければならない。本日は非常に反省すべき一日だった。', 0, 0, 'manual', NULL, '2010-03-16 00:00:00', '2017-02-03 14:14:45'),
(55, 'diary', '2010-03-17', '2010-03-17 00:00:00', '昨日の影響を少しひきずってしまい、午前の奉仕にでる熱意が全くおきず。晩の奉仕へ行くつもりであったが眠りすぎによりよく眠ることができず、結局奉仕を休んでしまう。よくない一日であった。', 0, 0, 'manual', NULL, '2010-03-17 00:00:00', '2017-02-03 14:14:45'),
(56, 'diary', '2010-03-18', '2010-03-18 00:00:00', '夜勤明け。朝からマラソンを行う。午前中はTODOをこなす。また太田兄弟がWORDを教わりに来る。午後ねる。晩から集会へ。その後再度TODOをこなす。', 0, 0, 'manual', NULL, '2010-03-18 00:00:00', '2017-02-03 14:14:45'),
(57, 'diary', '2010-03-19', '2010-03-19 00:00:00', '朝からマラソン２KM.家事を中心にTODOをこなす。午後は何をしたか覚えていないが、無駄に時間を使ったかと思われる。夕方からマンガ倉庫は長い時間立ち読みをする。霊的なTODOは必ずこなさねばならない。', 0, 0, 'manual', NULL, '2010-03-19 00:00:00', '2017-02-03 14:14:45'),
(58, 'diary', '2010-03-20', '2010-03-20 00:00:00', '午前中は奉仕に出る。午後は少し休んだ後、仕事に備えて寝る。夜勤。最低でも霊的なTODOはこなすべきだった。', 0, 0, 'manual', NULL, '2010-03-20 00:00:00', '2017-02-03 14:14:45'),
(59, 'diary', '2010-03-21', '2010-03-21 00:00:00', '夜勤明け。非常にハードな一日。午前中は集会。夕方から奉仕。深夜から仕事。合間に寝ることにするが寝つきが悪かったり、腕の関節痛に悩まされる。奉仕では気落ちする。よくない反応と疲れから満足行く証言ができなかった。いそがしすぎる一日のためTODOは一切できず。', 0, 0, 'manual', NULL, '2010-03-21 00:00:00', '2017-02-03 14:14:45'),
(60, 'diary', '2010-03-22', '2010-03-22 00:00:00', '夜勤明け。多少疲れていたが、午前中は家事を中心にTODOをこなす。午後は娯楽に費やす。裕介とマンガ倉庫へ行く。WEB漫画を読みすぎて時間が取られる。母から電話があり、真は一緒に暮らしてもよいとのこと。現在一人暮らしらしい。', 0, 0, 'manual', NULL, '2010-03-22 00:00:00', '2017-02-03 14:14:45'),
(61, 'diary', '2010-03-23', '2010-03-23 00:00:00', '一日中TODOをこなす。合間にマンガを読んだりして過ごす。有意義に時間を過ごしたはずなのだが何か物足りない。せっかくの完全な休みなのだからどこかへ出かければ良かった。しかし金がない・・・。', 0, 0, 'manual', NULL, '2010-03-23 00:00:00', '2017-02-03 14:14:45'),
(62, 'diary', '2010-03-24', '2010-03-24 00:00:00', '午前中はマラソンなどTODOをこなす。午後の早い時間には就寝し、仕事に備える。', 0, 0, 'manual', NULL, '2010-03-24 00:00:00', '2017-02-03 14:14:45'),
(63, 'diary', '2010-03-25', '2010-03-25 00:00:00', '夜勤８時まで。ストレスが若干たまっているため午前中はゲームをするがもっと早く寝るべきだった。午後から寝ることにするが2、3時間しか眠れず。間接痛みと胃腸の調子が悪い。そのまま6時から集会。割り当ては上手くできた。毎日練習してよかった。集会後何とか一時間半だけ寝ることができたが、関節は痛む。不調のまま仕事へ。仕事から帰ってすぐ寝るべきだった。', 0, 0, 'manual', NULL, '2010-03-25 00:00:00', '2017-02-03 14:14:45'),
(64, 'diary', '2010-03-26', '2010-03-26 00:00:00', '仕事で過剰に疲れてしまう。この日は疲れをとることに専念する。午前10時までゲームをして過越し、その後寝る。午後4時におき家事を行う。午後6時には寝る。肉体的な疲れは精神的な疲れも呼び起こすが、霊的なTODOだけでもせめて行うべきだった。', 0, 0, 'manual', NULL, '2010-03-26 00:00:00', '2017-02-03 14:14:45'),
(65, 'diary', '2010-03-27', '2010-03-27 00:00:00', '午前4時ごろに目が覚める。まどろんだあと、TODOをこなす。疲れがいまだ残っている。今日は奉仕は休み午後の仕事に備えねばなるまい。また今の仕事を5月いっぱいでやめることにする。', 0, 0, 'manual', NULL, '2010-03-27 00:00:00', '2017-02-03 14:14:45'),
(66, 'diary', '2010-03-28', '2010-03-28 00:00:00', '夜勤明けですぐ集会。午後は寝て夕方から奉仕、また寝て仕事。ハードなスケジュールだがよく寝られたおかげでそれほど苦とは感じず。夜勤明けの集会後できるだけ寝ないようにすることがポイント。長時間よく寝られないでいるよりも短時間の集中睡眠が効果的。', 0, 0, 'manual', NULL, '2010-03-28 00:00:00', '2017-02-03 14:14:45'),
(67, 'diary', '2010-03-29', '2010-03-29 00:00:00', '夜勤明け。一日中、漫画を読みすぎてしまう。大変反省すべき一日。おもしろいものに時間をたくさんとられるようなことをはしてはならず。おもしろい娯楽も何かをしながらやるようにしたい。夕方に寝て翌日4時半ごろ起きる。', 0, 0, 'manual', NULL, '2010-03-29 00:00:00', '2017-02-03 14:14:45'),
(68, 'diary', '2010-03-30', '2010-03-30 00:00:00', '午前中を無駄に時間を過ごしてしまう。TODOも半分くらいしかできていない。夕方は記念式。その後本部へ行く。', 0, 0, 'manual', NULL, '2010-03-30 00:00:00', '2017-02-03 14:14:45'),
(69, 'diary', '2010-03-31', '2010-03-31 00:00:00', '朝の時間をダラダラ過ごしすぎてします。午後はカツウ岳に登山。その後図書館へ行き、アワ岳の登山ルートやタイワンキンギョの生息地などを確認する。タイワンキンギョは昭和までは羽地大川にいたが、すでに絶滅したと思われる。あとはカンナフクチ川と億首川くらいか・・・。', 0, 0, 'manual', NULL, '2010-03-31 00:00:00', '2017-02-03 14:14:45'),
(70, 'diary', '2010-04-01', '2010-04-01 00:00:00', '朝早くおきるがゆったりと時間を過ごす。髪を切ってもらう。正午ごろ北谷へ帰る。午後はすぐ寝る。夕方集会。そして仕事。　やはり昨日のうちで北谷へ帰っておくべきだった。TODOができず白米もないため、精神面、肉体面で影響が出てしまった。', 0, 0, 'manual', NULL, '2010-04-01 00:00:00', '2017-02-03 14:14:45'),
(71, 'diary', '2010-04-02', '2010-04-02 00:00:00', '夜勤明け。かなり疲れているので帰ってすぐ寝る。午後3時ごろ起き午後5時半まで無駄に過ごす。まだ疲れていたため寝る。仕事前に白米を食べるべきであった。あとTODOを行う習慣がとぎれてしまったため、よくない一日を過ごしてしまう。', 0, 0, 'manual', NULL, '2010-04-02 00:00:00', '2017-02-03 14:14:45'),
(72, 'diary', '2010-04-03', '2010-04-03 00:00:00', '深夜1時半ごろ起床。茶を飲み、時折休憩をはさみながらTODOを行う。茶を飲むと疲れが取れるような気がする。', 0, 0, 'manual', NULL, '2010-04-03 00:00:00', '2017-02-03 14:14:45'),
(73, 'diary', '2010-04-04', '2010-04-04 00:00:00', '夜勤明けで、その後集会。その後寝て、そして奉仕へ。自分のミスで姉妹に迷惑がかかり、非常に気落ちする奉仕だった。このあと雨に濡れたことと、今日のことを忘れるためゲームをひたすらするが、体が冷えてしまい風邪の兆候を示す。', 0, 0, 'manual', NULL, '2010-04-04 00:00:00', '2017-02-03 14:14:45'),
(74, 'diary', '2010-04-05', '2010-04-05 00:00:00', '風の兆候あり。TODOをほとんど行わず、ゲームばかりすごす良くない一日であった。晩から風邪気味の状態で仕事。', 0, 0, 'manual', NULL, '2010-04-05 00:00:00', '2017-02-03 14:14:45'),
(75, 'diary', '2010-04-06', '2010-04-06 00:00:00', '風邪をひき熱もある。午前中は寝る。午後から裕介の書類を取りに役場へ。ついでに買い物。このため症状が悪化。晩から裕介の群れの劇の練習があった。本日はTODOをこなせず。', 0, 0, 'manual', NULL, '2010-04-06 00:00:00', '2017-02-03 14:14:45'),
(76, 'diary', '2010-04-07', '2010-04-07 00:00:00', 'あるていど風邪は良くなる。まだ症状あり。一日中、娯楽ばかり行ったため著しく霊性も低下する。TODOと心をこめた祈りをすべきだった。', 0, 0, 'manual', NULL, '2010-04-07 00:00:00', '2017-02-03 14:14:45'),
(77, 'diary', '2010-04-08', '2010-04-08 00:00:00', 'まだ風邪の症状が残る。TODOを行いつつひたすら水分を取る治療を行う。午後くらいから寝る。仕事前に起きるがまだ気管支辺りが晴れているようだ。', 0, 0, 'manual', NULL, '2010-04-08 00:00:00', '2017-02-03 14:14:45'),
(78, 'diary', '2010-04-09', '2010-04-09 00:00:00', '夜勤明け。何とか仕事をこなす。本日TODO等ができず無駄に時間を過ごしていまう。療養のため夕方ごろに寝る。', 0, 0, 'manual', NULL, '2010-04-09 00:00:00', '2017-02-03 14:14:45'),
(79, 'diary', '2010-04-10', '2010-04-10 00:00:00', '風邪はだいぶ治ってきたがまだ不調。', 0, 0, 'manual', NULL, '2010-04-10 00:00:00', '2017-02-03 14:14:45'),
(80, 'diary', '2010-04-11', '2010-04-11 00:00:00', '不調。集会を休む。ひたすら寝る。', 0, 0, 'manual', NULL, '2010-04-11 00:00:00', '2017-02-03 14:14:45'),
(81, 'diary', '2010-04-12', '2010-04-12 00:00:00', '夜勤明け。ゲームと家事を行い、ほかのことがあまりできず。役場へ行き買い物をすればもうすでに疲れ果てている。風邪や昨日の影響もあり、肉体的な疲れと慢性的な気力低下が問題。TODOができず。', 0, 0, 'manual', NULL, '2010-04-12 00:00:00', '2017-02-03 14:14:45'),
(82, 'diary', '2010-04-13', '2010-04-13 00:00:00', 'TODOできず。熱意が下がっている状態。風邪による霊的低下や、心に残る不安などが原因だがそれ以外にもあるかと。真は受け入れを表明。母もそれを支持。熱意をだいぶ取り戻す。祈りと熱意の個人研究により気力を取り戻す必要がある。', 0, 0, 'manual', NULL, '2010-04-13 00:00:00', '2017-02-03 14:14:45'),
(83, 'diary', '2010-04-14', '2010-04-14 00:00:00', 'TODOを中途半端にしか行えなかった。原因はゲームのしすぎ。やる気の低下。熱意を保つための個人研究とゲームは時間を決めて行うようにする。本日夜勤明け。', 0, 0, 'manual', NULL, '2010-04-14 00:00:00', '2017-02-03 14:14:45'),
(84, 'diary', '2010-04-15', '2010-04-15 00:00:00', '朝から雷。TODOを中途半端に。祈る習慣が必要。', 0, 0, 'manual', NULL, '2010-04-15 00:00:00', '2017-02-03 14:14:45'),
(85, 'diary', '2010-04-16', '2010-04-16 00:00:00', '夜勤明け。午前中を無駄に時間を浪費してしまう。正午頃に寝る。夜勤。', 0, 0, 'manual', NULL, '2010-04-16 00:00:00', '2017-02-03 14:14:45'),
(86, 'diary', '2010-04-17', '2010-04-17 00:00:00', '疲れと著しい熱意の低下。何をしたかさえ覚えておらず。スランプ気味である。', 0, 0, 'manual', NULL, '2010-04-17 00:00:00', '2017-02-03 14:14:45'),
(87, 'diary', '2010-04-18', '2010-04-18 00:00:00', '午前は集会。疲れているため午後は寝る。夕方から群れの交わりに参加。奉仕へ行けず。疲れと熱意の低下。', 0, 0, 'manual', NULL, '2010-04-18 00:00:00', '2017-02-03 14:14:45'),
(88, 'diary', '2010-04-19', '2010-04-19 00:00:00', '午前中をだらだらと過ごしてしまう。午後から本部へ行き、ゆっくりと過ごす。', 0, 0, 'manual', NULL, '2010-04-19 00:00:00', '2017-02-03 14:14:45'),
(89, 'diary', '2010-04-20', '2010-04-20 00:00:00', '本部に滞在中。車を治してもらい、さらに資格の受験料を貸してもらう。午後は寝る。無駄に過ごす時間があった。', 0, 0, 'manual', NULL, '2010-04-20 00:00:00', '2017-02-03 14:14:45'),
(90, 'diary', '2010-04-21', '2010-04-21 00:00:00', '夜勤明け。午前中は寝る。午後はゲームをしすぎるてしまう。夕方からTODOをこなす。熱意が著しく低下しているため奉仕にいけず。対策としてひたすらTODOをこなし続けることにより、熱意を強める。水風呂も効果的である。', 0, 0, 'manual', NULL, '2010-04-21 00:00:00', '2017-02-03 14:14:45'),
(91, 'diary', '2010-04-22', '2010-04-22 00:00:00', '熱意の著しい低下。スランプに陥っている', 0, 0, 'manual', NULL, '2010-04-22 00:00:00', '2017-02-03 14:14:45'),
(92, 'diary', '2010-04-23', '2010-04-23 00:00:00', '熱意の著しい低下。スランプに陥っている', 0, 0, 'manual', NULL, '2010-04-23 00:00:00', '2017-02-03 14:14:45'),
(93, 'diary', '2010-04-24', '2010-04-24 00:00:00', '熱意の著しい低下。スランプに陥っている', 0, 0, 'manual', NULL, '2010-04-24 00:00:00', '2017-02-03 14:14:45'),
(94, 'diary', '2010-04-25', '2010-04-25 00:00:00', 'スランプに陥っている。集会で励まされ、奉仕へ行けた。今晩仕事休みであるのもあるが。晩に会衆お別れ会。', 0, 0, 'manual', NULL, '2010-04-25 00:00:00', '2017-02-03 14:14:45'),
(95, 'diary', '2010-04-26', '2010-04-26 00:00:00', '熱意の著しい低下。スランプに陥っている', 0, 0, 'manual', NULL, '2010-04-26 00:00:00', '2017-02-03 14:14:45'),
(96, 'diary', '2010-04-27', '2010-04-27 00:00:00', '熱意の著しい低下。スランプに陥っている', 0, 0, 'manual', NULL, '2010-04-27 00:00:00', '2017-02-03 14:14:45'),
(97, 'diary', '2010-04-28', '2010-04-28 00:00:00', '熱意の著しい低下。スランプに陥っている。何もする気がおきずゲームばかりしている。夜から仕事。スランプから抜け出すために祈りつつ打開を図る。', 0, 0, 'manual', NULL, '2010-04-28 00:00:00', '2017-02-03 14:14:45'),
(98, 'diary', '2010-04-29', '2010-04-29 00:00:00', '夜勤明け。熱意向上の秘訣。向上心を持つ。午前中にＴＯＤＯを行う。午後は眠り、晩から山里会衆分会前最後の集会。すべてのＴＯＤＯをこなすようにしたい。', 0, 0, 'manual', NULL, '2010-04-29 00:00:00', '2017-02-03 14:14:45'),
(99, 'diary', '2010-04-30', '2010-04-30 00:00:00', '朝からマラソンなどＴＯＤＯをこなす。しかし、途中から漫画を読みすぎてしまい時間を無駄に過ごしてしまう。資格勉強がここしばらくできていない。時間がとりにくい。時間の取られる娯楽はしないほうが良い。するにしてもＴＯＤＯをすべて終えてから。夜仕事。', 0, 0, 'manual', NULL, '2010-04-30 00:00:00', '2017-02-03 14:14:45'),
(100, 'diary', '2010-05-09', '2010-05-09 00:00:00', '夜勤明け。集会。奉仕に行く勇気がどうしても出なかったが、なんとか祈りのうちに奉仕に出かける。しかし、集合場所にはだれもいなかった。雨で中止か少し遅れたので別の場所に移動したか・・・。', 0, 0, 'manual', NULL, '2010-05-09 00:00:00', '2017-02-03 14:14:45'),
(101, 'diary', '2010-05-11', '2010-05-11 00:00:00', '夜勤明け。ひどく疲れたため寝る時間が多し。晩から家族研究を行う。空いた時間は無駄に過ごしてしまう。', 0, 0, 'manual', NULL, '2010-05-11 00:00:00', '2017-02-03 14:14:45'),
(102, 'diary', '2010-05-12', '2010-05-12 00:00:00', '午前中はダラダラと過ごし、夕方から集会。そして晩から仕事', 0, 0, 'manual', NULL, '2010-05-12 00:00:00', '2017-02-03 14:14:45'),
(103, 'diary', '2010-05-13', '2010-05-13 00:00:00', '夜勤明け。午前中は寝る。午後から本部へ。', 0, 0, 'manual', NULL, '2010-05-13 00:00:00', '2017-02-03 14:14:45'),
(104, 'diary', '2010-05-14', '2010-05-14 00:00:00', '本部にて母の買い物と水汲みに付き合う。ついでに水草を羽地大川で採取。荷物片付けを行う。水槽セッティングをしミドリフグを一匹いれる。', 0, 0, 'manual', NULL, '2010-05-14 00:00:00', '2017-02-03 14:14:45'),
(105, 'diary', '2010-05-15', '2010-05-15 00:00:00', '午前中は父と祖父のいる施設を訪ねる。また片付けなどを行う。午後は寝て晩から仕事。', 0, 0, 'manual', NULL, '2010-05-15 00:00:00', '2017-02-03 14:14:45'),
(106, 'diary', '2010-05-16', '2010-05-16 00:00:00', '夜勤明け。集会へ。午後は群れの交わり。その後はダラダラと時間を過ごし、夕方には寝る。', 0, 0, 'manual', NULL, '2010-05-16 00:00:00', '2017-02-03 14:14:45'),
(107, 'diary', '2010-05-17', '2010-05-17 00:00:00', '夜中中ゲームをし過ぎる。午前は寝る。午後は予習を行いながらゲームをする。晩に寝て、深夜は仕事', 0, 0, 'manual', NULL, '2010-05-17 00:00:00', '2017-02-03 14:14:45'),
(108, 'diary', '2010-05-18', '2010-05-18 00:00:00', '午前中は霊的ＴＯＤＯとゲームをする。そして寝る。', 0, 0, 'manual', NULL, '2010-05-18 00:00:00', '2017-02-03 14:14:45'),
(109, 'diary', '2010-06-01', '2010-06-01 00:00:00', '今日の朝でファミマの仕事を終える。', 0, 0, 'manual', NULL, '2010-06-01 00:00:00', '2017-02-03 14:14:45'),
(110, 'diary', '2010-06-02', '2010-06-02 00:00:00', '試験勉強', 0, 0, 'manual', NULL, '2010-06-02 00:00:00', '2017-02-03 14:14:45'),
(111, 'diary', '2010-06-03', '2010-06-03 00:00:00', 'UMLモデリング技能認定試験L2を受けるが不合格', 0, 0, 'manual', NULL, '2010-06-03 00:00:00', '2017-02-03 14:14:45'),
(112, 'diary', '2010-06-04', '2010-06-04 00:00:00', '引越し準備をしながらダラダラすごす。', 0, 0, 'manual', NULL, '2010-06-04 00:00:00', '2017-02-03 14:14:45'),
(113, 'diary', '2010-06-06', '2010-06-06 00:00:00', '午前中集会。', 0, 0, 'manual', NULL, '2010-06-06 00:00:00', '2017-02-03 14:14:45'),
(114, 'diary', '2010-06-07', '2010-06-07 00:00:00', '本部にて引越し準備', 0, 0, 'manual', NULL, '2010-06-07 00:00:00', '2017-02-03 14:14:45'),
(115, 'diary', '2010-06-08', '2010-06-08 00:00:00', '本部から北谷へ。', 0, 0, 'manual', NULL, '2010-06-08 00:00:00', '2017-02-03 14:14:45'),
(116, 'diary', '2010-06-09', '2010-06-09 00:00:00', '午前中は引越し準備と試験勉強。晩は奥原家にて食事招待。', 0, 0, 'manual', NULL, '2010-06-09 00:00:00', '2017-02-03 14:14:45'),
(117, 'diary', '2010-06-10', '2010-06-10 00:00:00', '試験勉強。', 0, 0, 'manual', NULL, '2010-06-10 00:00:00', '2017-02-03 14:14:45'),
(118, 'diary', '2010-06-11', '2010-06-11 00:00:00', '午前中にUMLモデリング技能認定試験L1を受け合格する。晩の集会では祈りと割当を行う。', 0, 0, 'manual', NULL, '2010-06-11 00:00:00', '2017-02-03 14:14:45'),
(119, 'diary', '2010-06-12', '2010-06-12 00:00:00', '昼は引越し準備。夜は食事招待。山川姉妹より餞別５０００円もらう。', 0, 0, 'manual', NULL, '2010-06-12 00:00:00', '2017-02-03 14:14:45'),
(120, 'diary', '2010-06-13', '2010-06-13 00:00:00', '北谷での最後の集会。昼は玉城兄弟の食事招待、夜は橋田兄弟の食事招待。山崎兄弟から手紙とお金をもらいイナミネリサさんとユキさんからハンカチをもらう。', 0, 0, 'manual', NULL, '2010-06-13 00:00:00', '2017-02-03 14:14:45'),
(121, 'diary', '2010-06-14', '2010-06-14 00:00:00', '最後の本部。引越し準備を行う。久田兄弟からネクタイをもらう。', 0, 0, 'manual', NULL, '2010-06-14 00:00:00', '2017-02-03 14:14:45'),
(122, 'diary', '2010-06-15', '2010-06-15 00:00:00', '沖縄から名古屋へ引越し', 0, 0, 'manual', NULL, '2010-06-15 00:00:00', '2017-02-03 14:14:45'),
(123, 'diary', '2010-06-16', '2010-06-16 00:00:00', '散策をする。自転車を買う', 0, 0, 'manual', NULL, '2010-06-16 00:00:00', '2017-02-03 14:14:45'),
(124, 'diary', '2010-06-17', '2010-06-17 00:00:00', '荷物が届く。PC設定や部屋の片づけを行う。', 0, 0, 'manual', NULL, '2010-06-17 00:00:00', '2017-02-03 14:14:45'),
(125, 'diary', '2010-06-18', '2010-06-18 00:00:00', '部屋の片付けを行うが基本的にダラダラと一日を過ごしてしまう。', 0, 0, 'manual', NULL, '2010-06-18 00:00:00', '2017-02-03 14:14:45'),
(126, 'diary', '2010-06-19', '2010-06-19 00:00:00', '遅くまで寝て、夕方までゲームという良くない生活。しかし午後は悔い改めてさまざまなめんどうなやるべきことやTODOを行う。', 0, 0, 'manual', NULL, '2010-06-19 00:00:00', '2017-02-03 14:14:45'),
(127, 'diary', '2010-06-20', '2010-06-20 00:00:00', '中央会衆に初出席。とても対応が早かった。午後はゲームをしすぎたのが反省。時間を決めてやるようにしたい。晩は買い物とTODOを行う。', 0, 0, 'manual', NULL, '2010-06-20 00:00:00', '2017-02-03 14:14:45'),
(128, 'diary', '2010-07-18', '2010-07-18 00:00:00', 'ここ一ヶ月やるべきことが一切できなかった。仕事も見つからず。ゲームのやりすぎも原因だがTODOを行う習慣ができていなかったのが原因。徐々に熱意はそがれてゆき、集会へ行く気力まで失われてしまった。あとかなり遅寝遅起状態なのもよくない。とにかくTODOは必ず毎日おこなう。また奉仕へ行こうとする気力は聖霊による助けが必要であり、聖霊を悲しませるような行動をとらないようにしなければならない。', 0, 0, 'manual', NULL, '2010-07-18 00:00:00', '2017-02-03 14:14:45'),
(129, 'diary', '2010-07-19', '2010-07-19 00:00:00', '午前中から午後の早い時間帯はムダに時間を過ごしてしまった。夕方からはTODOを行う。とある新聞サイトは見てはならないことにする。', 0, 0, 'manual', NULL, '2010-07-19 00:00:00', '2017-02-03 14:14:45'),
(130, 'diary', '2010-07-20', '2010-07-20 00:00:00', '午前中はプログラミングを行う。午後はTODOをする。TODOを行うときは、今していることが気力の強化訓練となっていることを意識する。　夕方に図書館へ行くが場所が分からず。晩は音楽を聴いてい過ごすが、時間が取られすぎた。相変わらず遅寝。', 0, 0, 'manual', NULL, '2010-07-20 00:00:00', '2017-02-03 14:14:45');
INSERT INTO `diary_as` (`id`, `category`, `diary_date`, `diary_dt`, `diary_note`, `sort_no`, `delete_flg`, `update_user`, `ip_addr`, `created`, `modified`) VALUES
(131, 'diary', '2010-07-21', '2010-07-21 00:00:00', '午前中は10時ごろに起き、プログラミングをする。午後はTODOを行う。晩は生き抜きとして世界の民謡を世界から探す。本日は比較的に良いときをすごすごとができたが、晩の音楽鑑賞等をだらだらと長時間にわたり無駄に過ごしてしまう。遅寝遅起も問題。', 0, 0, 'manual', NULL, '2010-07-21 00:00:00', '2017-02-03 14:14:45'),
(132, 'diary', '2010-07-22', '2010-07-22 00:00:00', '本日は11時に起床し、2時間ほどプログラミングをする。その後、TODOを行う。またバイトルドットコムで仕事の申し込みを行う。とりあえず一週間ほど待つ。毎日仕事サイトをチェックし、最新のものに飛びつくようにしたい。一日の終わりに最新仕事チェックを入れる。夕方までＴＯＤＯを行い、その後、気落ちし、疲れて眠ってしまう。夜に起きてＴＯＤＯを引き続き行う。', 0, 0, 'manual', NULL, '2010-07-22 00:00:00', '2017-02-03 14:14:45'),
(133, 'diary', '2010-07-23', '2010-07-23 00:00:00', '本日も11時おきと遅い。昨日申し込んだ仕事はパチンコに関する仕事であったため、断る。引き続きほかの仕事も探してみるが今回は見当たらず。なんとか仕事に付かねばならないが、まだ少し時間があるので落ち着くようにしたい。一応本日もＴＯＤＯを行う。晩は集会。プログラマと製造系の仕事はほとんどないようだ。どうすべきだろうか。', 0, 0, 'manual', NULL, '2010-07-23 00:00:00', '2017-02-03 14:14:45'),
(134, 'diary', '2010-07-24', '2010-07-24 00:00:00', '本日9時半におきるが一晩中、腕や足の関節が痛む。一応プログラミングを朝は行い、昼も行う。不調から昼寝もする。熱意も低下する。奉仕にはいけなかった。明日こそは奉仕にでなければ・・・。', 0, 0, 'manual', NULL, '2010-07-24 00:00:00', '2017-02-03 14:14:45'),
(135, 'diary', '2010-07-25', '2010-07-25 00:00:00', '午前は集会。午後は奉仕と良い一日を送る。ＴＯＤＯが中途半端であったことが痛い。', 0, 0, 'manual', NULL, '2010-07-25 00:00:00', '2017-02-03 14:14:45'),
(136, 'diary', '2010-07-26', '2010-07-26 00:00:00', '役場へ転入届けを出す。遺跡発掘の仕事に応募する。ハローワークへ行き、3社に応募する。書類を送るのは金曜日が良い。ＴＯＤＯがすべて行えなかったのは良くなかった。熱意を高く保つ助けとなるので疲れていても行うようにする。', 0, 0, 'manual', NULL, '2010-07-26 00:00:00', '2017-02-03 14:14:45'),
(137, 'diary', '2010-07-27', '2010-07-27 00:00:00', '案の定、熱意が低下してしまい、遅くまで寝てしまい、しばらく何もできず。とりあえずプログラミングを行う。発掘調査の会社から連絡があり、紹介状が必要とのことなのでハローワークまで取りに行く。午後はＴＯＤＯを行う。', 0, 0, 'manual', NULL, '2010-07-27 00:00:00', '2017-02-03 14:14:45'),
(138, 'diary', '2010-07-28', '2010-07-28 00:00:00', '午後は山崎Ｓに食事招待をしてもらい、4時に発掘調査のパートの面接。', 0, 0, 'manual', NULL, '2010-07-28 00:00:00', '2017-02-03 14:14:45'),
(139, 'diary', '2010-07-29', '2010-07-29 00:00:00', '一日中、気分が不安定で、だらだらとＴＯＤＯをこなしたりしていうちに気づけば夜だった。ＴＯＤＯは迅速に行う必要がある。', 0, 0, 'manual', NULL, '2010-07-29 00:00:00', '2017-02-03 14:14:45'),
(140, 'diary', '2010-07-30', '2010-07-30 00:00:00', '昨晩、眠れず、過度の眠気により大会へ行けなかった。発掘調査のパートが採用になった。午後は口座開設に行く。午後は明日の大会の準備などいろいろ行う。', 0, 0, 'manual', NULL, '2010-07-30 00:00:00', '2017-02-03 14:14:45'),
(141, 'diary', '2010-07-31', '2010-07-31 00:00:00', '本日、ナゴヤドームで大会に出席する。17000人という大人数だった。帰ってからムダに時間を過ごしてしまう時間があったので反省せねばならない。今回の大会で時間とエネルギーを娯楽などさまざまな事柄で浪費しないようにすることを学んだ。', 0, 0, 'manual', NULL, '2010-07-31 00:00:00', '2017-02-03 14:14:45'),
(142, 'diary', '2010-08-02', '2010-08-02 00:00:00', '本日から仕事。画像処理の仕事を行う。ただ気分がひたすら暗い。自分でもよく分からないが、ストレスを強く感じている。', 0, 0, 'manual', NULL, '2010-08-02 00:00:00', '2017-02-03 14:14:45'),
(143, 'diary', '2010-08-03', '2010-08-03 00:00:00', '本日の仕事は昨日ほどのストレスはなかった。午後からプログラムの仕事ができると思ったが、結局一月ほど先延ばしになってしまった。明日から何をするのか不安である。', 0, 0, 'manual', NULL, '2010-08-03 00:00:00', '2017-02-03 14:14:45'),
(144, 'diary', '2010-08-04', '2010-08-04 00:00:00', '本日の仕事は画像編集をする。体臭を指摘されかなりショックを受けるが、対処はしたので必要以上に考えないようにする。この職場はいいのか悪いのか良く分からない。ただ神に頼り、知恵のうちに行動すべきだろう。', 0, 0, 'manual', NULL, '2010-08-04 00:00:00', '2017-02-03 14:14:45'),
(145, 'diary', '2010-08-05', '2010-08-05 00:00:00', '本日の仕事も画像編集。とりあえず今日はあるていど良かった。熱心に祈った結果かもしれない。しかし今の生活が精神的に持つだろうか？マイナス思考はやめよう。', 0, 0, 'manual', NULL, '2010-08-05 00:00:00', '2017-02-03 14:14:45'),
(146, 'diary', '2010-08-08', '2010-08-08 00:00:00', '本日、運転免許の更新のため集会にいけず。相当の人数がいたため多少遅れても大丈夫だった。次回は集会後に更新を受けるようにしたい。免許更新の帰りに豊明市によってみる。前回きちんとみて回れなかったので、いろいろ見て回る。ザリガニを取った池など。朝の祈りと集会へ行かなかったせいか帰ってからは良くない一日を過ごした。', 0, 0, 'manual', NULL, '2010-08-08 00:00:00', '2017-02-03 14:14:45'),
(147, 'diary', '2010-08-09', '2010-08-09 00:00:00', '本日の仕事はストレスが比較的に少なかった。家に帰ってからのわずかな時間を有意義に使うようにしたい。', 0, 0, 'manual', NULL, '2010-08-09 00:00:00', '2017-02-03 14:14:45'),
(148, 'diary', '2010-08-10', '2010-08-10 00:00:00', '昨日と同様の一日', 0, 0, 'manual', NULL, '2010-08-10 00:00:00', '2017-02-03 14:14:45'),
(149, 'diary', '2010-08-14', '2010-08-14 00:00:00', 'ストレス解消のためゲームを一日中やりすぎる。', 0, 0, 'manual', NULL, '2010-08-14 00:00:00', '2017-02-03 14:14:45'),
(150, 'diary', '2010-08-15', '2010-08-15 00:00:00', '午前集会。午後奉仕。リラックスするよう勤めるとそれほど苦にならない。', 0, 0, 'manual', NULL, '2010-08-15 00:00:00', '2017-02-03 14:14:45'),
(151, 'diary', '2010-08-16', '2010-08-16 00:00:00', '今日も仕事。少しずつストレスは軽減されている。リラックスするように勤める。', 0, 0, 'manual', NULL, '2010-08-16 00:00:00', '2017-02-03 14:14:45'),
(152, 'diary', '2010-08-17', '2010-08-17 00:00:00', '今日も仕事だが家で有効な時間の使い方ができず。なんとかしなければ。', 0, 0, 'manual', NULL, '2010-08-17 00:00:00', '2017-02-03 14:14:45'),
(153, 'diary', '2010-08-18', '2010-08-18 00:00:00', '昨日と同様の一日', 0, 0, 'manual', NULL, '2010-08-18 00:00:00', '2017-02-03 14:14:45'),
(154, 'diary', '2010-08-19', '2010-08-19 00:00:00', '昨日と同様の一日　時間が過ぎるのがあまりにも早く感じる。', 0, 0, 'manual', NULL, '2010-08-19 00:00:00', '2017-02-03 14:14:45'),
(155, 'diary', '2010-08-20', '2010-08-20 00:00:00', '仕事、そして集会。明日は猿投山へ行く予定だったが準備不足のため中止する。', 0, 0, 'manual', NULL, '2010-08-20 00:00:00', '2017-02-03 14:14:45'),
(156, 'diary', '2010-08-21', '2010-08-21 00:00:00', '一日中ゲームをしてストレス解消を試みる。多少は気分転換になったが霊的な熱意は回復しない。', 0, 0, 'manual', NULL, '2010-08-21 00:00:00', '2017-02-03 14:14:45'),
(157, 'diary', '2010-08-22', '2010-08-22 00:00:00', '霊的な熱意が著しく低下したため集会の予習すらできず、奉仕にもでることができない。ゲームをしながら家事を行う。憂鬱感を感じ始めている。', 0, 0, 'manual', NULL, '2010-08-22 00:00:00', '2017-02-03 14:14:45'),
(158, 'diary', '2010-08-23', '2010-08-23 00:00:00', '仕事は問題なし。霊的な熱意がかなり低下している。学習意欲も非常に低い上に時間も非常に少ない。家に帰っても一瞬のうちに時間が過ぎてしまう。研究のための時間もあまりとれず。テレビとネットサーフィンをさけたほうが良いかもしれない。とりあえずテレビをつけるのをやめよう。個人研究をしながら娯楽を楽しむ体制をとろうと思う。', 0, 0, 'manual', NULL, '2010-08-23 00:00:00', '2017-02-03 14:14:45'),
(159, 'diary', '2010-09-16', '2010-09-16 00:00:00', '9/6よりプログラマの仕事になる。最初はやる気満々であったが、後から苦しい。時間が少ないことが原因。なんとかリラックスしながら集中して仕事に取り組もう。', 0, 0, 'manual', NULL, '2010-09-16 00:00:00', '2017-02-03 14:14:45'),
(160, 'diary', '2010-10-04', '2010-10-04 00:00:00', '9月後半は非常に欝気味の生活を送る。昨日は集会すらいけなかった。原因は毎日のすべきことを行わなかったことになる。だらだらとパチンコをする状態のような無気力を引き起こす生活が良くなかったのだろう。今日から成すべきことを記録をつけつつやろうと思う。', 0, 0, 'manual', NULL, '2010-10-04 00:00:00', '2017-02-03 14:14:45'),
(161, 'diary', '2010-10-05', '2010-10-05 00:00:00', '昨日、良い生活を送ったおかげで、多少充足感あり。この調子で正しい生活を。積極的な精神は気力を充実させる。快楽は気力を低下させる。　仕事も順調。心臓の痛みもなくなってきた。　本日真沖縄から帰ってくる。　', 0, 0, 'manual', NULL, '2010-10-05 00:00:00', '2017-02-03 14:14:45'),
(162, 'diary', '2010-10-07', '2010-10-07 00:00:00', '少し疲れとストレスを感じる。しかしそれでもなすべきことをなす正しい生活を送っている状態。', 0, 0, 'manual', NULL, '2010-10-07 00:00:00', '2017-02-03 14:14:45'),
(163, 'diary', '2010-11-03', '2010-11-03 00:00:00', '[結婚]する人たちは自分の肉身に患難を招くでしょう。←日々の聖句の記録の間違い。\r\n', 0, 0, 'manual', NULL, '2010-11-03 00:00:00', '2017-02-03 14:14:45'),
(164, 'diary', '2010-12-14', '2010-12-14 00:00:00', '10月と11月はストレスに悩まされ続ける。集会へ行く気力すらなく、休む日も多くなった。12月初旬によくなってきたと思ったが再び調子が悪くなってきた。　生活にパターンをつけていかねばならない', 0, 0, 'manual', NULL, '2010-12-14 00:00:00', '2017-02-03 14:14:45'),
(165, 'diary', '2010-12-15', '2010-12-15 00:00:00', 'あさって割当。', 0, 0, 'manual', NULL, '2010-12-15 00:00:00', '2017-02-03 14:14:45'),
(166, 'diary', '2011-01-03', '2011-01-03 00:00:00', '去年は、前半は良かったが、後半とくに年末近くは非常によくない。やるべきことをやることが必要だ。今年の8月くらいに沖縄へ帰る予定だが、このままでは帰れず。まずは、とにかく毎日のなすべきことを行うことが大事。定期的な集会出席と奉仕活動を。\r\n今年の目標。早寝早起、毎日の日課を行う。定期的な集会出席、週に一回は奉仕に出る。集会で一回は注解をする。', 0, 0, 'manual', NULL, '2011-01-03 00:00:00', '2017-02-03 14:14:45'),
(167, 'diary', '2011-01-04', '2011-01-04 00:00:00', '連休終わり。やるべきことができず。自分を鍛錬することが熱意につながるのでなすべきことをせねばならない。', 0, 0, 'manual', NULL, '2011-01-04 00:00:00', '2017-02-03 14:14:45'),
(168, 'diary', '2011-01-05', '2011-01-05 00:00:00', 'ダイエットに励む必要がある。それには熱意が必要であり、そのために忍耐強さが必要。忍耐強さには日々の訓練が大切', 0, 0, 'manual', NULL, '2011-01-05 00:00:00', '2017-02-03 14:14:45'),
(169, 'diary', '2011-01-06', '2011-01-06 00:00:00', '胃腸風邪にかかり、いきたえだえで仕事をする。', 0, 0, 'manual', NULL, '2011-01-06 00:00:00', '2017-02-03 14:14:45'),
(170, 'diary', '2011-01-07', '2011-01-07 00:00:00', '仕事を休む。集会も休む', 0, 0, 'manual', NULL, '2011-01-07 00:00:00', '2017-02-03 14:14:45'),
(171, 'diary', '2011-01-08', '2011-01-08 00:00:00', 'なにもせず無駄に時間を過ごしてしまう。気力が病的にまで低下。', 0, 0, 'manual', NULL, '2011-01-08 00:00:00', '2017-02-03 14:14:45'),
(172, 'diary', '2011-01-09', '2011-01-09 00:00:00', '集会へ行くこともできず。気力がかなり低下。', 0, 0, 'manual', NULL, '2011-01-09 00:00:00', '2017-02-03 14:14:45'),
(173, 'diary', '2011-01-10', '2011-01-10 00:00:00', 'すべきことを少しずつ行い、霊的に少し回復。ひさしぶりに過去写真をみて気力を少し取り戻す。', 0, 0, 'manual', NULL, '2011-01-10 00:00:00', '2017-02-03 14:14:45'),
(174, 'diary', '2011-01-11', '2011-01-11 00:00:00', '仕事の内容が難しい。勤務表スケジュール作成システム。デスマーチに踏み入れぬよう慎重になる必要がある。胃腸の調子が相変わらず悪い。', 0, 0, 'manual', NULL, '2011-01-11 00:00:00', '2017-02-03 14:14:45'),
(175, 'diary', '2011-01-12', '2011-01-12 00:00:00', '相変わらず仕事が難しい。少しづつ進んではいるが・・・。記録をとりつつ、重要部分を先にやっておくのがよいだろう。', 0, 0, 'manual', NULL, '2011-01-12 00:00:00', '2017-02-03 14:14:45'),
(176, 'diary', '2011-01-17', '2011-01-17 00:00:00', '胃腸の調子がやっと戻ってきた。相変わらず仕事の内容が難しい。ある程度作るべきものが見えてきたが・・・。　　　金曜日は集会へ出席できたが、日曜日は熱意を極度に失い、出席できなかった。土曜日を目的もなくダラダラと過ごしたのが原因。　土曜日はどこかへ出かけねば。', 0, 0, 'manual', NULL, '2011-01-17 00:00:00', '2017-02-03 14:14:45'),
(177, 'diary', '2011-02-07', '2011-02-07 00:00:00', 'ハブかまれ記念日（退院）　土曜日は淡水魚水族かんへ行こう', 0, 0, 'manual', NULL, '2011-02-07 00:00:00', '2017-02-03 14:14:45'),
(178, 'diary', '2011-03-14', '2011-03-14 00:00:00', '本日からやるべきことをやる訓練を行う。なんとしてでも霊的に回復せねばならない。\r\n毎日行う事柄を必ずやる。　１１日に東日本大震災あり。非常に激しいもので、ショックを与えるものだった。\r\n東海地震も近いうちに起こるだろう。後悔しないためにも霊的な事柄をしっかりと行ってゆきたい。', 0, 0, 'manual', NULL, '2011-03-14 00:00:00', '2017-02-03 14:14:45'),
(179, 'diary', '2011-03-14', '2011-03-14 00:00:00', '本日からやるべきことをやる訓練を行う。なんとしてでも霊的に回復せねばならない。\r\n毎日行う事柄を必ずやる。　１１日に東日本大震災あり。非常に激しいもので、ショックを与えるものだった。\r\n東海地震も近いうちに送るだろう。後悔しないためにも霊的な事柄をしっかりと行ってゆきたい。', 0, 0, 'manual', NULL, '2011-03-14 00:00:00', '2017-02-03 14:14:45'),
(180, 'diary', '2011-03-15', '2011-03-15 00:00:00', '今日もやるべきことを行う。家に帰ったらすぐにやるべき事を行いたい。少し暖かくなった。原発事故がまた起こる。見た目よりも被害はなさそうだが・・・。', 0, 0, 'manual', NULL, '2011-03-15 00:00:00', '2017-02-03 14:14:45'),
(181, 'diary', '2011-03-16', '2011-03-16 00:00:00', 'とにかく、成すべき事を成す。これが自己鍛錬となり、気力の元になる。地震の被害者がぞくぞくと増えている。死者不明者が１万を超えた。東海地震も油断ならない。', 0, 0, 'manual', NULL, '2011-03-16 00:00:00', '2017-02-03 14:14:45'),
(182, 'diary', '2011-03-17', '2011-03-17 00:00:00', '今日はなすべきことを成すのを遅れた。テレビをつけっぱにしていたのがまずかった。仕事のほうが仕様変更が大幅に増え厄介なことになった。', 0, 0, 'manual', NULL, '2011-03-17 00:00:00', '2017-02-03 14:14:45'),
(183, 'diary', '2011-03-18', '2011-03-18 00:00:00', '仕事が大変だった。自分が役に立てのは珍しいことだ。集会に遅れたが出席することができた。やるべきことも行っている。週末まで気を抜かない。', 0, 0, 'manual', NULL, '2011-03-18 00:00:00', '2017-02-03 14:14:45'),
(184, 'diary', '2011-03-19', '2011-03-19 00:00:00', '日中だらだらと無駄に過ごしてしまった。ゲームをしすぎた。もっと有益な時間のすごさねば。だらだとしすぎたせいでやるべきことをできなくなるところだった。遅くまでおきることになった。', 0, 0, 'manual', NULL, '2011-03-19 00:00:00', '2017-02-03 14:14:45'),
(185, 'diary', '2011-03-19', '2011-03-19 00:00:00', '日中だらだらと無駄に過ごしてしまった。ゲームをしすぎた。もっと有益な時間のすごさねば。だらだとしすぎたせいでやるべきことをできなくなるところだった。遅くまでおきることになった。', 0, 0, 'manual', NULL, '2011-03-19 00:00:00', '2017-02-03 14:14:45'),
(186, 'diary', '2011-03-20', '2011-03-20 00:00:00', '集会へ行き、さらに奉仕へ行けたのは高評価。しかしやるべきことができなかった。', 0, 0, 'manual', NULL, '2011-03-20 00:00:00', '2017-02-03 14:14:45'),
(187, 'diary', '2011-03-21', '2011-03-21 00:00:00', '午前中だらだらと過ごしてしまう。せっかくの休みなのだからもっと有意義に時間を用いるべきだった。午後からは買い物とかする。やるべきことも行うが時間遅くになってしまう。早めに行動を取らねば。', 0, 0, 'manual', NULL, '2011-03-21 00:00:00', '2017-02-03 14:14:45'),
(188, 'diary', '2011-03-24', '2011-03-24 00:00:00', '少したるんできている。やるべきことはすぐに行うようにしないといけない。時間を決めて行動すべきだろうか？', 0, 0, 'manual', NULL, '2011-03-24 00:00:00', '2017-02-03 14:14:45'),
(189, 'diary', '2011-03-27', '2011-03-27 00:00:00', '日曜日は食事招待をしてもらう。', 0, 0, 'manual', NULL, '2011-03-27 00:00:00', '2017-02-03 14:14:45'),
(190, 'diary', '2011-03-28', '2011-03-28 00:00:00', 'とりあえずやるべき事を行おうとしたが、あまりできず', 0, 0, 'manual', NULL, '2011-03-28 00:00:00', '2017-02-03 14:14:45'),
(191, 'diary', '2011-03-29', '2011-03-29 00:00:00', '外部の要因で集中しづらいときは、集中できる音楽を聴きながら画よい。', 0, 0, 'manual', NULL, '2011-03-29 00:00:00', '2017-02-03 14:14:45'),
(192, 'diary', '2011-04-10', '2011-04-10 00:00:00', '先週はなすべきことがほとんどできていない週だった。週末にその付けが来た。', 0, 0, 'manual', NULL, '2011-04-10 00:00:00', '2017-02-03 14:14:45'),
(193, 'diary', '2011-04-12', '2011-04-12 00:00:00', 'なんか疲れた。物事を行う気力があまりなく、ただ無気力が・・・。仕事が忙しいから？　気晴らしが必要なのだろうか？　ただ倦怠感が非常に強い', 0, 0, 'manual', NULL, '2011-04-12 00:00:00', '2017-02-03 14:14:45'),
(194, 'diary', '2011-05-03', '2011-05-03 00:00:00', '昨日、琵琶湖へ行く。タナゴはいなかった。なぞの稚魚を２匹捕獲。　４月はやるべきことをほとんど行っていなかっため、気力など著しくて低下。毎日行うべきことを確実にやらねばならない。伝道活動にも出られなかった。', 0, 0, 'manual', NULL, '2011-05-03 00:00:00', '2017-02-03 14:14:45'),
(195, 'diary', '2011-05-05', '2011-05-05 00:00:00', '昨日、猿投山に登った。４度目の成功。達成感あり。土曜日も登ろうかな。　やるべきことを一切やっていないことについては反省せねばならない。最低限、聖書通読、日々の聖句、日誌はやらねばならず。', 0, 0, 'manual', NULL, '2011-05-05 00:00:00', '2017-02-03 14:14:45'),
(196, 'diary', '2011-05-08', '2011-05-08 00:00:00', '牧羊訪問。曽我部兄弟と望月兄弟が来る。とりあえず優先して、聖書通読、日々の聖句、日誌を行い、あと食事にも気をつける。', 0, 0, 'manual', NULL, '2011-05-08 00:00:00', '2017-02-03 14:14:45'),
(197, 'diary', '2011-05-09', '2011-05-09 00:00:00', '風邪気味', 0, 0, 'manual', NULL, '2011-05-09 00:00:00', '2017-02-03 14:14:45'),
(198, 'diary', '2011-05-10', '2011-05-10 00:00:00', '風で会社を休む。このかぜはなかなか治らず。', 0, 0, 'manual', NULL, '2011-05-10 00:00:00', '2017-02-03 14:14:45'),
(199, 'diary', '2011-05-11', '2011-05-11 00:00:00', '風邪がまだ治らず。少しはよくなったが。', 0, 0, 'manual', NULL, '2011-05-11 00:00:00', '2017-02-03 14:14:45'),
(200, 'diary', '2011-05-20', '2011-05-20 00:00:00', 'まだ風邪。ひさしぶりに集会へ行く。', 0, 0, 'manual', NULL, '2011-05-20 00:00:00', '2017-02-03 14:14:45'),
(201, 'diary', '2011-05-21', '2011-05-21 00:00:00', '部屋の掃除などすべきことを行う。山へ行きたかったがすべきことをすることが優先', 0, 0, 'manual', NULL, '2011-05-21 00:00:00', '2017-02-03 14:14:45'),
(202, 'diary', '2011-05-30', '2011-05-30 00:00:00', '精神的にも肉体的にも疲れてしまい、集会へ行くことができなかった。異常なまで無気力感にどう立ち向かえるのか？　とにかく成すべきことをなすこと、十分な睡眠、エホバに奉仕と集会に出席できるようたゆまず祈る。', 0, 0, 'manual', NULL, '2011-05-30 00:00:00', '2017-02-03 14:14:45'),
(203, 'diary', '2011-06-11', '2011-06-11 00:00:00', '著しい気力低下と過度の精神的な疲れにより、金曜の集会を休む。会社にいるときにすでに気力は散漫だった。日曜日こそは集会へ行かねばならない。毎日の行うことを必ずやらないといけないのだが。', 0, 0, 'manual', NULL, '2011-06-11 00:00:00', '2017-02-03 14:14:45'),
(204, 'diary', '2011-06-18', '2011-06-18 00:00:00', 'やるべきことを行うペースが週１になってしまっている。精神状態もかなり不安定。おそらく、社会不安障害が再発した可能性あり。集会へ行くのをやめてしまおうかと考えるほど。東京にいたときのようになってしまうのも時間の問題。山に行く気も起こらないことから、精神的な異常があると分かる。まだ軽度のうちに治療を試みる。ひきつづき毎日行うべき事柄を行う。', 0, 0, 'manual', NULL, '2011-06-18 00:00:00', '2017-02-03 14:14:45'),
(205, 'diary', '2011-06-20', '2011-06-20 00:00:00', 'ＴＯＤＯ管理ツールをある程度完成させる。これを友好的に活用したい。', 0, 0, 'manual', NULL, '2011-06-20 00:00:00', '2017-02-03 14:14:45'),
(206, 'diary', '2011-06-21', '2011-06-21 00:00:00', '今日もＴＯＤＯを行う。１つのＴＯＤＯを短時間で終わらせるようにしよう。', 0, 0, 'manual', NULL, '2011-06-21 00:00:00', '2017-02-03 14:14:45'),
(207, 'diary', '2011-06-22', '2011-06-22 00:00:00', '今日もＴＯＤＯを行う。少し気分が良くなる。', 0, 0, 'manual', NULL, '2011-06-22 00:00:00', '2017-02-03 14:14:45'),
(208, 'diary', '2011-06-23', '2011-06-23 00:00:00', '今日もＴＯＤＯを行う。家から帰ってすぐＴＯＤＯを行うようにしたい。', 0, 0, 'manual', NULL, '2011-06-23 00:00:00', '2017-02-03 14:14:45'),
(209, 'diary', '2011-06-25', '2011-06-25 00:00:00', '昨日は仕事でかなり疲れてしまい集会へ行けず。前の晩にコーラを飲みすぎ、睡眠が不順分だったのが原因かと思われる。本日はだらだらと時間をすごす。夜はTODOを大量に行う。', 0, 0, 'manual', NULL, '2011-06-25 00:00:00', '2017-02-03 14:14:45'),
(210, 'diary', '2011-07-02', '2011-07-02 00:00:00', '集会へ行くのに失敗したのが原因で今週は気力が大幅に低下。昨日も集会へ行けず。ＳＡＤがひどくなり会社まで行けず。なんとかせねばならない。明日はなんとしてでも集会と奉仕にでよう。とりあえずＳＡＤがある程度良くなるまで交わりは断る方針でいく。', 0, 0, 'manual', NULL, '2011-07-02 00:00:00', '2017-02-03 14:14:45'),
(211, 'diary', '2011-07-04', '2011-07-04 00:00:00', '精神状況ひどく、集会にも行けず。', 0, 0, 'manual', NULL, '2011-07-04 00:00:00', '2017-02-03 14:14:45'),
(212, 'diary', '2011-07-10', '2011-07-10 00:00:00', '集会へ行くことができなかった。金曜はかろうじて足を向けたがとちゅうで遅刻に気づき結局引き返す。日曜日はすでに土曜の前の晩の時点で心が折れていた。今週の金曜は割当もある。おまけに仕事も忙しくなりそう。完全にＳＡＤになってしまっている・・・。対策をなんとか', 0, 0, 'manual', NULL, '2011-07-10 00:00:00', '2017-02-03 14:14:45'),
(213, 'diary', '2011-07-13', '2011-07-13 00:00:00', '今日は少し調子いい。精神的に苦しいことを行い、精神の強化を図ってみる。', 0, 0, 'manual', NULL, '2011-07-13 00:00:00', '2017-02-03 14:14:45'),
(214, 'diary', '2011-07-14', '2011-07-14 00:00:00', '少しずつ調子を取り戻す。ＴＯＤＯを毎日こなしたのが効果が出てきたのだと思う。また個人研究によるの大きい。わずかな研究時間だが得るものは大きい。明日はなんとしてでも集会に出よう。', 0, 0, 'manual', NULL, '2011-07-14 00:00:00', '2017-02-03 14:14:45'),
(215, 'diary', '2011-07-20', '2011-07-20 00:00:00', 'やっと金曜日と日曜日に集会へ行けた。次は奉仕を目標に。すべき事柄が怠けがちなので気を引き締める。', 0, 0, 'manual', NULL, '2011-07-20 00:00:00', '2017-02-03 14:14:45'),
(216, 'diary', '2011-07-23', '2011-07-23 00:00:00', '昨日も集会へ行くことができたのは良かった。辛いことを行うことにより精神を訓練する。今週の疲れがでる。体力的に疲れてはいるが、なすべきをなし精神訓練とする。', 0, 0, 'manual', NULL, '2011-07-23 00:00:00', '2017-02-03 14:14:45'),
(217, 'diary', '2011-07-30', '2011-07-30 00:00:00', '今週はＴＯＤＯをまったくやってない。原因はすぐに始めないこと。最初の一歩を。昨日から地域大会だが、昨日は仕事で、本日は疲れで休む。明日はいけねばならない', 0, 0, 'manual', NULL, '2011-07-30 00:00:00', '2017-02-03 14:14:45'),
(218, 'diary', '2011-08-06', '2011-08-06 00:00:00', '今週ＴＯＤＯさぼりがちである。少しだらける。明日は奉仕へ行くことができるだろうか？', 0, 0, 'manual', NULL, '2011-08-06 00:00:00', '2017-02-03 14:14:45'),
(219, 'diary', '2011-08-13', '2011-08-13 00:00:00', '著しい気力のたるみ。仕事がハードすぎて反動がいっきにでた。', 0, 0, 'manual', NULL, '2011-08-13 00:00:00', '2017-02-03 14:14:45'),
(220, 'diary', '2011-08-22', '2011-08-22 00:00:00', '仕事の忙しさにより、精神が蝕まれている。SAD。成すべきことを怠りがちなのも原因。集会へまたいけなくなっている。とにかくTODOを毎日やるしかない。　お盆中は水族館くらい。土曜日は知多半島をドライブしようとしたが雨で引き返す。　やるべきことをなし、奉仕へも行くことができたなら、山へ登ろう。御嶽山とかいいな。', 0, 0, 'manual', NULL, '2011-08-22 00:00:00', '2017-02-03 14:14:45'),
(221, 'diary', '2011-08-23', '2011-08-23 00:00:00', '昨日は仕事でかなり疲れた。今日も同様に疲れたが、霊的に良いせいか、それほどまでは苦にならず。毎日の日課をこなすことは大切だ。万歩計を買って散歩するようにしよう。', 0, 0, 'manual', NULL, '2011-08-23 00:00:00', '2017-02-03 14:14:45'),
(222, 'diary', '2011-08-24', '2011-08-24 00:00:00', '特に無し。適当にTODOを行う。少し疲れはある。', 0, 0, 'manual', NULL, '2011-08-24 00:00:00', '2017-02-03 14:14:45'),
(223, 'diary', '2011-09-01', '2011-09-01 00:00:00', '最近疲れ気味。まずい状況。', 0, 0, 'manual', NULL, '2011-09-01 00:00:00', '2017-02-03 14:14:45'),
(224, 'diary', '2011-09-05', '2011-09-05 00:00:00', 'またも集会へ行けず。非常に気持ちが不安定。とりあえず毎日行うべきことやらねば。', 0, 0, 'manual', NULL, '2011-09-05 00:00:00', '2017-02-03 14:14:45'),
(225, 'diary', '2011-09-26', '2011-09-26 00:00:00', 'ここ何週間も不調。集会へもいけず。少しづつでもやるべきことをやる。いや、むしろ少しずつ', 0, 0, 'manual', NULL, '2011-09-26 00:00:00', '2017-02-03 14:14:45'),
(226, 'diary', '2011-09-27', '2011-09-27 00:00:00', '動機を吟味', 0, 0, 'manual', NULL, '2011-09-27 00:00:00', '2017-02-03 14:14:45'),
(227, 'diary', '2011-10-16', '2011-10-16 00:00:00', '毎週のように集会へ行こうと努力するが、いまだ行けず。原因はストレスからの無気力症候群とＴＯＤＯを行わなかったこと。ＴＯＤＯの負担を少し減らす。', 0, 0, 'manual', NULL, '2011-10-16 00:00:00', '2017-02-03 14:14:45'),
(228, 'diary', '2011-11-07', '2011-11-07 00:00:00', 'ダメだ・・・。とにかく集会へ行かねば。ＳＡＤ状態から抜け出せず。', 0, 0, 'manual', NULL, '2011-11-07 00:00:00', '2017-02-03 14:14:45'),
(229, 'diary', '2011-12-11', '2011-12-11 00:00:00', 'ひさびさにTODOを開始する。TODOを実施が力になることを忘れないようにする。先週の日曜はやっと集会へいけた。しかし、今回は行けなかった。', 0, 0, 'manual', NULL, '2011-12-11 00:00:00', '2017-02-03 14:14:45'),
(230, 'diary', '2012-01-07', '2012-01-07 00:00:00', '先日養老山に登頂に成功。また集会へ行けず。なんとか明日はいけるようにしたい。', 0, 0, 'manual', NULL, '2012-01-07 00:00:00', '2017-02-03 14:14:45'),
(231, 'diary', '2012-02-01', '2012-02-01 00:00:00', 'ハブ咬まれ記念日なので、４万するハンディGPSを購入。', 0, 0, 'manual', NULL, '2012-02-01 00:00:00', '2017-02-03 14:14:45'),
(232, 'diary', '2012-04-20', '2012-04-20 00:00:00', '3月いっぱいで仕事をやめる。今後のことについて選択肢が多い。まずは集会。', 0, 0, 'manual', NULL, '2012-04-20 00:00:00', '2017-02-03 14:14:45'),
(233, 'diary', '2012-04-22', '2012-04-22 00:00:00', '集会へまた行けず。毎日すべきことを行い、高い気力を常時保たねばならない。', 0, 0, 'manual', NULL, '2012-04-22 00:00:00', '2017-02-03 14:14:45'),
(234, 'diary', '2012-04-27', '2012-04-27 00:00:00', '無気力状態。無駄に時間を過ごすのをやめねばならない。毎日行うべきことを必ず行う。', 0, 0, 'manual', NULL, '2012-04-27 00:00:00', '2017-02-03 14:14:45'),
(235, 'diary', '2012-04-28', '2012-04-28 00:00:00', 'ＴＯＤＯを行うと、集会へ行こうとする気持ちも大きくなる。', 0, 0, 'manual', NULL, '2012-04-28 00:00:00', '2017-02-03 14:14:45'),
(236, 'diary', '2012-05-04', '2012-05-04 00:00:00', '今日は集会へゆくことができた。祈りとTODOを行なったことが大きい。', 0, 0, 'manual', NULL, '2012-05-04 00:00:00', '2017-02-03 14:14:45'),
(237, 'diary', '2012-05-06', '2012-05-06 00:00:00', '集会へゆくことができず。正しくTODOができなかったのが原因。前日の段階で疲れていた。', 0, 0, 'manual', NULL, '2012-05-06 00:00:00', '2017-02-03 14:14:45'),
(238, 'diary', '2012-05-08', '2012-05-08 00:00:00', '養老算の登山に行く予定が昨晩寝ることができず、中止に。', 0, 0, 'manual', NULL, '2012-05-08 00:00:00', '2017-02-03 14:14:45'),
(239, 'diary', '2012-05-09', '2012-05-09 00:00:00', '本日天気は曇り。タナゴ釣りに行きたいものだ。', 0, 0, 'manual', NULL, '2012-05-09 00:00:00', '2017-02-03 14:14:45'),
(240, 'diary', '2012-05-11', '2012-05-11 00:00:00', '木曽川へ魚を取りに行く。カワムツらしきサカナを採取したが、輸送中にしなせてしまった。ハゼとスジエビも１ひきづつ捕まえる。', 0, 0, 'manual', NULL, '2012-05-11 00:00:00', '2017-02-03 14:14:45'),
(241, 'diary', '2012-05-12', '2012-05-12 00:00:00', '無気力状態になり、午前中と昼間は何もできず。', 0, 0, 'manual', NULL, '2012-05-12 00:00:00', '2017-02-03 14:14:45'),
(242, 'diary', '2012-05-15', '2012-05-15 00:00:00', '時々疲れが出る。このようなときは、１時間の睡眠をとる。１５分程度の仮眠でも効果ある。本日雨、明日は晴れるので山にでも登りたい。しかしやらねばならぬことはたくさんある。', 0, 0, 'manual', NULL, '2012-05-15 00:00:00', '2017-02-03 14:14:45'),
(243, 'diary', '2012-05-16', '2012-05-16 00:00:00', '図書館へ行く。ダイエットを開始する。', 0, 0, 'manual', NULL, '2012-05-16 00:00:00', '2017-02-03 14:14:45'),
(244, 'diary', '2012-05-17', '2012-05-17 00:00:00', '有意義に過ごせているかと思う。やらねばならないことが多数あるため、やるべきことはある程度減らすか、一度に行う量を減らす必要がある。', 0, 0, 'manual', NULL, '2012-05-17 00:00:00', '2017-02-03 14:14:45'),
(245, 'diary', '2012-05-19', '2012-05-19 00:00:00', '昨日は集会へ行けなかった。TODOをこなした後、異様な虚無感に襲われた。自分への褒美が必要かと思われる。', 0, 0, 'manual', NULL, '2012-05-19 00:00:00', '2017-02-03 14:14:45'),
(246, 'diary', '2012-05-30', '2012-05-30 00:00:00', '先日、養老山へ登る。非常に良い場所を見つけた。いずれまた行きたい。', 0, 0, 'manual', NULL, '2012-05-30 00:00:00', '2017-02-03 14:14:45'),
(247, 'diary', '2012-05-31', '2012-05-31 00:00:00', 'TODOを完璧ではないがある程度こなせている。ただしこれからは、時間も考慮に入れる必要がある。', 0, 0, 'manual', NULL, '2012-05-31 00:00:00', '2017-02-03 14:14:45'),
(248, 'diary', '2012-06-07', '2012-06-07 00:00:00', '住民税１１万。非常に腹立たしいが払わねばなるまい・・・。日々プログラミングをしてすごす。', 0, 0, 'manual', NULL, '2012-06-07 00:00:00', '2017-02-03 14:14:45'),
(249, 'diary', '2012-06-08', '2012-06-08 00:00:00', '本部で新しいシャム猫を拾ったようだ。先日、ブルーギルが死ぬ。ウナギにすりよる可愛い魚だった。コーヒーを飲むとかなり気力が高ぶる。', 0, 0, 'manual', NULL, '2012-06-08 00:00:00', '2017-02-03 14:14:45'),
(250, 'diary', '2012-06-11', '2012-06-11 00:00:00', 'もうすぐ沖縄。霊的にも引き続き気を引き締める。あとダイエット。残り少ない名古屋、どこかへ行こうか・・・。', 0, 0, 'manual', NULL, '2012-06-11 00:00:00', '2017-02-03 14:14:45'),
(251, 'diary', '2012-06-12', '2012-06-12 00:00:00', 'ここ数日、ひたすらプログラミングを行う。定期的な聖書通読と日々の聖句がある程度行われている。回復してきたので、そろそろ霊的な方向性も定めたい。やるべきことが多く、一日が早く終わるように感じる。', 0, 0, 'manual', NULL, '2012-06-12 00:00:00', '2017-02-03 14:14:45'),
(252, 'diary', '2012-06-23', '2012-06-23 00:00:00', 'そろそろ引越し準備をする。ウナギに義経の名を授ける。仕事の準備もそろそろやらないとまずい。', 0, 0, 'manual', NULL, '2012-06-23 00:00:00', '2017-02-03 14:14:45'),
(253, 'diary', '2012-07-02', '2012-07-02 00:00:00', '先日、24H断食をする。気力の低下や体力的な低下があり思い切った行動ができないが、目に見えて体重が減った。胃の縮小もある。しかしストレスによる過食が発生してしまうというデメリットがある。本日10時よりセミ断食開始。', 0, 0, 'manual', NULL, '2012-07-02 00:00:00', '2017-02-03 14:14:45'),
(254, 'diary', '2012-07-03', '2012-07-03 00:00:00', '当初断食は24Hを予定するが延長する。目標72H。', 0, 0, 'manual', NULL, '2012-07-03 00:00:00', '2017-02-03 14:14:45'),
(255, 'diary', '2012-07-04', '2012-07-04 00:00:00', '用事のため、断食ダイエット４８Hで終了することにする。1～2kg減。', 0, 0, 'manual', NULL, '2012-07-04 00:00:00', '2017-02-03 14:14:45'),
(256, 'diary', '2012-07-05', '2012-07-05 00:00:00', '猿投山に登る。雨がちらほらと降る。回復機関を含めて、断食完了', 0, 0, 'manual', NULL, '2012-07-05 00:00:00', '2017-02-03 14:14:45'),
(257, 'diary', '2012-07-06', '2012-07-06 00:00:00', '昨日の疲れが残る。本日夕方より、断食開始予定。', 0, 0, 'manual', NULL, '2012-07-06 00:00:00', '2017-02-03 14:14:45'),
(258, 'diary', '2012-07-09', '2012-07-09 00:00:00', '明日は名古屋を発つ。山がなごり惜しい。今度は車に乗って旅をしたい。', 0, 0, 'manual', NULL, '2012-07-09 00:00:00', '2017-02-03 14:14:45'),
(259, 'diary', '2012-07-10', '2012-07-10 00:00:00', '名古屋を発ち大阪南港へ。9時間の道のりを原付で行く。ひどい日焼けをしてしまった。港への到着はギリギリだった。船に乗り那覇港へ。部屋は一部屋貸切状態であったので最高', 0, 0, 'manual', NULL, '2012-07-10 00:00:00', '2017-02-03 14:14:45'),
(260, 'diary', '2012-07-11', '2012-07-11 00:00:00', '船酔いで苦しむ。', 0, 0, 'manual', NULL, '2012-07-11 00:00:00', '2017-02-03 14:14:45'),
(261, 'diary', '2012-07-12', '2012-07-12 00:00:00', '那覇港到着。家路へ。帰るとさっそくおじいさんの病院へ。そして集会へ。', 0, 0, 'manual', NULL, '2012-07-12 00:00:00', '2017-02-03 14:14:45'),
(262, 'diary', '2012-07-13', '2012-07-13 00:00:00', '役所へ住所変更手続きと原付の名義変更を行う。', 0, 0, 'manual', NULL, '2012-07-13 00:00:00', '2017-02-03 14:14:45'),
(263, 'diary', '2012-07-14', '2012-07-14 00:00:00', '父さんと釣りへ。めずらしく船酔いは軽度ですんだ。アンカーを落としたがカワハギはたくさん釣れた。', 0, 0, 'manual', NULL, '2012-07-14 00:00:00', '2017-02-03 14:14:45'),
(264, 'diary', '2012-07-15', '2012-07-15 00:00:00', '集会へ。午後は奉仕にもでる。', 0, 0, 'manual', NULL, '2012-07-15 00:00:00', '2017-02-03 14:14:45'),
(265, 'diary', '2012-07-16', '2012-07-16 00:00:00', 'いろいろと忙しい。名古屋ではＳＡＤに苦しめられたが、沖縄に戻ると一気に回復したように思える。奉仕ですらスムーズに行えるようになるとは自分でも驚きである。やはり自分には沖縄があっているのだろう。あと、本部会衆から引っ越すことはしないことを決意。', 0, 0, 'manual', NULL, '2012-07-16 00:00:00', '2017-02-03 14:14:45'),
(266, 'diary', '2012-07-17', '2012-07-17 00:00:00', 'いろいろと忙しい。', 0, 0, 'manual', NULL, '2012-07-17 00:00:00', '2017-02-03 14:14:45'),
(267, 'diary', '2012-07-18', '2012-07-18 00:00:00', '午前中は奉仕に行く。裕介が来る。', 0, 0, 'manual', NULL, '2012-07-18 00:00:00', '2017-02-03 14:14:45'),
(268, 'diary', '2012-07-19', '2012-07-19 00:00:00', 'いろいろと忙しい。', 0, 0, 'manual', NULL, '2012-07-19 00:00:00', '2017-02-03 14:14:45'),
(269, 'diary', '2012-07-20', '2012-07-20 00:00:00', '裕介帰る。', 0, 0, 'manual', NULL, '2012-07-20 00:00:00', '2017-02-03 14:14:45'),
(270, 'diary', '2012-07-21', '2012-07-21 00:00:00', '体調が悪い。家でなるべく安静にしてすごす。', 0, 0, 'manual', NULL, '2012-07-21 00:00:00', '2017-02-03 14:14:45'),
(271, 'diary', '2012-07-22', '2012-07-22 00:00:00', '集会へ行く。寝過ごしてしまい、奉仕へ行きそびれる。熱意が低下してしまったのが原因。', 0, 0, 'manual', NULL, '2012-07-22 00:00:00', '2017-02-03 14:14:45'),
(272, 'diary', '2012-07-23', '2012-07-23 00:00:00', '適当にTODOをしたり、犬のえさなどをお使いに行ったり。', 0, 0, 'manual', NULL, '2012-07-23 00:00:00', '2017-02-03 14:14:45'),
(273, 'diary', '2012-07-24', '2012-07-24 00:00:00', '午後から、オオジャナのおばあさんの家へ行く。オオジャナのおじいさんは病院から退院できるのだろうか？', 0, 0, 'manual', NULL, '2012-07-24 00:00:00', '2017-02-03 14:14:45'),
(274, 'diary', '2012-07-25', '2012-07-25 00:00:00', '寝過ごし、奉仕へ行きそびれる。祖父の入院先から連絡があり、父と病院へ。おじいさんが飯を食わなくなったとかだったが、父が食わせると良く食べた。', 0, 0, 'manual', NULL, '2012-07-25 00:00:00', '2017-02-03 14:14:45'),
(275, 'diary', '2012-07-26', '2012-07-26 00:00:00', '現在おばあさんが住んでいる土地名義をおじいさんから父へ変更するため司法書士へ行く。おじいさんがまともに返事ができない状態だった。返事を聞くため司法書士と病院まで行くが、とてもじゃないがいたたまれない状況。', 0, 0, 'manual', NULL, '2012-07-26 00:00:00', '2017-02-03 14:14:45'),
(276, 'diary', '2012-07-27', '2012-07-27 00:00:00', '午前中は適当にTODOを行った後、湧川へ啓と奉仕に出かける。午後は名義変更に必要な書類を取りに行くが、委任状が必要なため取りに行けず。午後はTODOを行う。犬を散歩させ、啓車バッテリー充電。', 0, 0, 'manual', NULL, '2012-07-27 00:00:00', '2017-02-03 14:14:45'),
(277, 'diary', '2012-07-28', '2012-07-28 00:00:00', '本日はTODOを行うことにする。さてはて本日はどこかへ気分転換しに行こうかな？', 0, 0, 'manual', NULL, '2012-07-28 00:00:00', '2017-02-03 14:14:45'),
(278, 'diary', '2012-07-29', '2012-07-29 00:00:00', '午前は集会へ。午後は奉仕へ。夕方は畑仕事。疲れたがやりがいがあった。Web開発も少しできればよかった。', 0, 0, 'manual', NULL, '2012-07-29 00:00:00', '2017-02-03 14:14:45'),
(279, 'diary', '2012-07-30', '2012-07-30 00:00:00', '体重がついに75Kg台になる。', 0, 0, 'manual', NULL, '2012-07-30 00:00:00', '2017-02-03 14:14:45'),
(280, 'diary', '2012-07-31', '2012-07-31 00:00:00', '午前中から３時まで、本部町役場、司法書士、今帰仁役場と用事を済ます。とりあえず名義変更の件は落ち着く。疲れたので少し昼寝し、夕方から夜にかけてカカズ兄弟の家のPC関連の設定等を行う。忙しい一日だった。', 0, 0, 'manual', NULL, '2012-07-31 00:00:00', '2017-02-03 14:14:45'),
(281, 'diary', '2012-08-01', '2012-08-01 00:00:00', '午前中は寝坊。また雨が降っていた。TODOを行った後、本部富士に登るが、岩がぬれて危険なため引き返す。晩は前原さんの家を訪れる。', 0, 0, 'manual', NULL, '2012-08-01 00:00:00', '2017-02-03 14:14:45'),
(282, 'diary', '2012-08-02', '2012-08-02 00:00:00', '午前中は関節痛で苦しむ。調子も悪い。午後はTODOを行い、集会へ。予習もしっかりとやっておけば良かった。', 0, 0, 'manual', NULL, '2012-08-02 00:00:00', '2017-02-03 14:14:45'),
(283, 'diary', '2012-08-03', '2012-08-03 00:00:00', '午前中は雨のため奉仕を休む。司法書士へ行き、名義変更書類を受け取る。', 0, 0, 'manual', NULL, '2012-08-03 00:00:00', '2017-02-03 14:14:45'),
(284, 'diary', '2012-08-04', '2012-08-04 00:00:00', '午前中は伊野波へ奉仕。帰ってからカツウ岳登山。良き見晴らしだった。TODOはしっかりとできず。', 0, 0, 'manual', NULL, '2012-08-04 00:00:00', '2017-02-03 14:14:45'),
(285, 'diary', '2012-08-05', '2012-08-05 00:00:00', '台風のため集会は休み。ダラダラとすごす。', 0, 0, 'manual', NULL, '2012-08-05 00:00:00', '2017-02-03 14:14:45'),
(286, 'diary', '2012-08-06', '2012-08-06 00:00:00', '台風が急成長を遂げたため、まだ台風の影響アリ。午前中は父に付き添いで病院＆見舞い＆買い物。おじいさんは相変わらず眠っている。退院できるのだろうか。午後はTODOを行う。', 0, 0, 'manual', NULL, '2012-08-06 00:00:00', '2017-02-03 14:14:45'),
(287, 'diary', '2012-08-07', '2012-08-07 00:00:00', '台風の影響で風や雨が強い。TODOを行いながら1日を過ごす。少しだらけてしまった。マンガを読み過ぎないようにしよう。', 0, 0, 'manual', NULL, '2012-08-07 00:00:00', '2017-02-03 14:14:45'),
(288, 'diary', '2012-08-08', '2012-08-08 00:00:00', '起きるのが遅すぎた。速く寝る必要がある。速く寝るために時間を決めて行動することも大切。午前中の遅くから、TODOを始める。母の預金依頼のためATMへ行く。夕方からは今帰仁の家へ農業。ピーマン、キュウリ、シソを植え、オリジナル農薬もまく。', 0, 0, 'manual', NULL, '2012-08-08 00:00:00', '2017-02-03 14:14:45'),
(289, 'diary', '2012-08-09', '2012-08-09 00:00:00', '午前中は、奉仕に行く。午後は特に覚えておらず。', 0, 0, 'manual', NULL, '2012-08-09 00:00:00', '2017-02-03 14:14:45'),
(290, 'diary', '2012-08-10', '2012-08-10 00:00:00', '地域大会', 0, 0, 'manual', NULL, '2012-08-10 00:00:00', '2017-02-03 14:14:45'),
(291, 'diary', '2012-08-11', '2012-08-11 00:00:00', '地域大会。オオジャナで宿泊', 0, 0, 'manual', NULL, '2012-08-11 00:00:00', '2017-02-03 14:14:45'),
(292, 'diary', '2012-08-12', '2012-08-12 00:00:00', '地域大会', 0, 0, 'manual', NULL, '2012-08-12 00:00:00', '2017-02-03 14:14:45'),
(293, 'diary', '2012-08-13', '2012-08-13 00:00:00', 'TODOを行いつつすごす。', 0, 0, 'manual', NULL, '2012-08-13 00:00:00', '2017-02-03 14:14:45'),
(294, 'diary', '2012-08-14', '2012-08-14 00:00:00', '特になし', 0, 0, 'manual', NULL, '2012-08-14 00:00:00', '2017-02-03 14:14:45'),
(295, 'diary', '2012-08-15', '2012-08-15 00:00:00', '寝坊し、奉仕の準備が間に合わず。雇用保険の手続きに行く。', 0, 0, 'manual', NULL, '2012-08-15 00:00:00', '2017-02-03 14:14:45'),
(296, 'diary', '2012-08-16', '2012-08-16 00:00:00', '午前はＴＯＤＯを行う。午後は名護に複数の用事を済ます。晩から集会。忙しい一日だった。', 0, 0, 'manual', NULL, '2012-08-16 00:00:00', '2017-02-03 14:14:45'),
(297, 'diary', '2012-08-17', '2012-08-17 00:00:00', '特になし', 0, 0, 'manual', NULL, '2012-08-17 00:00:00', '2017-02-03 14:14:45'),
(298, 'diary', '2012-08-18', '2012-08-18 00:00:00', '午前中は奉仕に行く。午後はＴＯＤＯ、畑、買い物', 0, 0, 'manual', NULL, '2012-08-18 00:00:00', '2017-02-03 14:14:45'),
(299, 'diary', '2012-08-19', '2012-08-19 00:00:00', '午前中は集会へ行く。午後は昼寝をやりすぎてしまう。夕方から畑仕事。21時より断食開始。', 0, 0, 'manual', NULL, '2012-08-19 00:00:00', '2017-02-03 14:14:45'),
(300, 'diary', '2012-08-20', '2012-08-20 00:00:00', '父の仕事の車がパンクしたため、トラックで救援に行く。午前中は無駄に過ごしてまう。', 0, 0, 'manual', NULL, '2012-08-20 00:00:00', '2017-02-03 14:14:45'),
(301, 'diary', '2012-08-21', '2012-08-21 00:00:00', 'TODOを主に行う。断食二日目はフラつく。晩に終了する。', 0, 0, 'manual', NULL, '2012-08-21 00:00:00', '2017-02-03 14:14:45'),
(302, 'diary', '2012-08-22', '2012-08-22 00:00:00', '体調わるい。午前中はＴＯＤＯを行い、午後はオオジャナのおじいさんのところへ。', 0, 0, 'manual', NULL, '2012-08-22 00:00:00', '2017-02-03 14:14:45'),
(303, 'diary', '2012-08-23', '2012-08-23 00:00:00', '断食を開始する。期間は１日のみ。数日の疲れを癒すため、ほぼ寝て過ごす。晩は集会', 0, 0, 'manual', NULL, '2012-08-23 00:00:00', '2017-02-03 14:14:45'),
(304, 'diary', '2012-08-24', '2012-08-24 00:00:00', '大型台風接近中。最近朝にすごく弱い。早寝せねばならず。', 0, 0, 'manual', NULL, '2012-08-24 00:00:00', '2017-02-03 14:14:45'),
(305, 'diary', '2012-08-25', '2012-08-25 00:00:00', '/DCIM/100ANDRO/DSC_0063.jpg', 0, 0, 'manual', NULL, '2012-08-25 00:00:00', '2017-02-03 14:14:45');
INSERT INTO `diary_as` (`id`, `category`, `diary_date`, `diary_dt`, `diary_note`, `sort_no`, `delete_flg`, `update_user`, `ip_addr`, `created`, `modified`) VALUES
(306, 'diary', '2012-08-26', '2012-08-26 00:00:00', '台風。ＴＯＤＯをして過ごす。中心気圧が910ＨＰというすさまじい数値を示している。14号までＵターンして接近してきた。', 0, 0, 'manual', NULL, '2012-08-26 00:00:00', '2017-02-03 14:14:45'),
(307, 'diary', '2012-08-27', '2012-08-27 00:00:00', '朝は引き続き台風。その間、部屋の大方付け。午後はボーリングへ参加。夕方はオバーに弁当を届けに行く。', 0, 0, 'manual', NULL, '2012-08-27 00:00:00', '2017-02-03 14:14:45'),
(308, 'diary', '2012-08-28', '2012-08-28 00:00:00', '午前中は適当にＴＯＤＯを行う。午後から、雇用保険の説明会へいく。', 0, 0, 'manual', NULL, '2012-08-28 00:00:00', '2017-02-03 14:14:45'),
(309, 'diary', '2012-08-29', '2012-08-29 00:00:00', '気力低下状態のため、奉仕へ行けず。12時まで寝てしまう。早寝早起きができていないのが原因。ＴＯＤＯすらできなかった。夕方に天底の畑に行き、ドラゴンフルーツを一株植える。', 0, 0, 'manual', NULL, '2012-08-29 00:00:00', '2017-02-03 14:14:45'),
(310, 'diary', '2012-08-30', '2012-08-30 00:00:00', '同じく遅起き。気力低下のため奉仕どころでなし。とりあえずＴＯＤＯを行う。レクの計画を立てようと思う。晩は集会。予習をちゃんとやっておけば良かった。', 0, 0, 'manual', NULL, '2012-08-30 00:00:00', '2017-02-03 14:14:45'),
(311, 'diary', '2012-08-31', '2012-08-31 00:00:00', '午前は奉仕。午後はトウギョを探しに大保ダムの下流域を調査。田で怪しい魚を発見するが手持ちの網では捕らえられなかった。ちなみに大保川の上流は水生昆虫専門のビオトープがある。', 0, 0, 'manual', NULL, '2012-08-31 00:00:00', '2017-02-03 14:14:45'),
(312, 'diary', '2012-09-01', '2012-09-01 00:00:00', 'ＴＯＤＯを行う。午後からオオジャナへ。お盆とかちあっていたため、オオジャナを離れ本屋へ退避。なぜ馬超はこのようなときにオオジャナへ行くのかと憤り覚えた。タツノリらに不快な思いをさせてしまったのが残念でならない。', 0, 0, 'manual', NULL, '2012-09-01 00:00:00', '2017-02-03 14:14:45'),
(313, 'diary', '2012-09-02', '2012-09-02 00:00:00', '午前は集会。', 0, 0, 'manual', NULL, '2012-09-02 00:00:00', '2017-02-03 14:14:45'),
(314, 'diary', '2012-09-03', '2012-09-03 00:00:00', '遅起き。ＴＯＤＯがほとんどできず。２時から職安、オジーの病院、今帰仁の畑にいそしむ。', 0, 0, 'manual', NULL, '2012-09-03 00:00:00', '2017-02-03 14:14:45'),
(315, 'diary', '2012-09-04', '2012-09-04 00:00:00', '一日中ＴＯＤＯを行う。プリオを車検に入れる。夕方は極度に疲れてしまい、関節が痛む', 0, 0, 'manual', NULL, '2012-09-04 00:00:00', '2017-02-03 14:14:45'),
(316, 'diary', '2012-09-05', '2012-09-05 00:00:00', '起きるのが遅くなる。田中姉妹の追悼式。奉仕に出る回数が少なすぎるので、ほぼ毎日出るくらいの決意を抱く。午後はＴＯＤＯ。おじいさんのいる病院に移動する。ビニールハウスの材料値段を見に行く。', 0, 0, 'manual', NULL, '2012-09-05 00:00:00', '2017-02-03 14:14:45'),
(317, 'diary', '2012-09-06', '2012-09-06 00:00:00', '午前はＴＯＤＯを行う。正午くらいに不要物を今帰仁へ持ってゆく。午後は疲れて眠ってしまう。晩は集会。集会後、極端に気落ちし、気力がひどく低下する。ゲームは時間を決めて行うようにしたい。', 0, 0, 'manual', NULL, '2012-09-06 00:00:00', '2017-02-03 14:14:45'),
(318, 'diary', '2012-09-07', '2012-09-07 00:00:00', '気力が低く、奉仕へ行けず。早起きしてＴＯＤＯを行う必要がある。明日は６時起き、水を一杯飲んで、ゲームを30分する。そして奉仕の準備。ＴＯＤＯを行うことにする。', 0, 0, 'manual', NULL, '2012-09-07 00:00:00', '2017-02-03 14:14:45'),
(319, 'diary', '2012-09-08', '2012-09-08 00:00:00', '午前中は、奉仕へ行く。午後はゆっくり過ごす。夕方には今帰仁へ。晩に藤本さんがお客としてくる。', 0, 0, 'manual', NULL, '2012-09-08 00:00:00', '2017-02-03 14:14:45'),
(320, 'diary', '2012-09-09', '2012-09-09 00:00:00', '午前中は集会。午後はダラダラと過ごしてしまう。夕方は畑。晩はＴＯＤＯを行う。', 0, 0, 'manual', NULL, '2012-09-09 00:00:00', '2017-02-03 14:14:45'),
(321, 'diary', '2012-09-10', '2012-09-10 00:00:00', '遅起きになってしまった。48時間の断食を開始するが、啓がおいしいドリアを作ったので中断。食べた後、再開。水曜日の朝まで。カメをもらう。コレステロールと名づける。午後は今帰仁へ荷物を持っていき、畑作業も行う。', 0, 0, 'manual', NULL, '2012-09-10 00:00:00', '2017-02-03 14:14:45'),
(322, 'diary', '2012-09-11', '2012-09-11 00:00:00', '今日は早起きする。午前はハローワークへ行き、雇用保険給付の認定をしに行く。帰りに今帰仁の畑へ寄り、キュウリとアオジソを植えて防虫ネットを張る。午後は気力低下と疲れにより昼寝する。起きても気力が回復せず、断食も中断する。母の買い物に時間をつぶされる。夜のおでんの誘惑にまけ、食べ過ぎる。', 0, 0, 'manual', NULL, '2012-09-11 00:00:00', '2017-02-03 14:14:45'),
(323, 'diary', '2012-09-12', '2012-09-12 00:00:00', '朝から気力が低下し奉仕に行けず。何もせずダラダラとゲームやインターネットをしたのが原因かと思われる。とにかくＴＯＤＯをしなければならない。午前はTODO。午後は畑へ行く。TODOの実行をもっとやらねばならず。', 0, 0, 'manual', NULL, '2012-09-12 00:00:00', '2017-02-03 14:14:45'),
(324, 'diary', '2012-09-13', '2012-09-13 00:00:00', '午前中はTODOを行う。おじいさんのいる病院へ行く。おじいさんの肺炎は良くならず、水が溜まっている状態。施設も退所することになった。晩は集会。半年以上の不活発が続いたため再研究をすることになり、気落ちする。', 0, 0, 'manual', NULL, '2012-09-13 00:00:00', '2017-02-03 14:14:45'),
(325, 'diary', '2012-09-14', '2012-09-14 00:00:00', '午前中は奉仕活動に出る。午後は今帰仁へ行く。明日、明後日あたり非常に強い台風が到来する。最大風速８０ｍもある。前回は70ｍもあったが実際には弱かった。この台風はどれくらい強いのだろうか。TODOがほとんどできず。健康保険税で５万近く払う。残り10万近くもある。', 0, 0, 'manual', NULL, '2012-09-14 00:00:00', '2017-02-03 14:14:45'),
(326, 'diary', '2012-09-15', '2012-09-15 00:00:00', '午前中は、台風対策に追われる。午後はダラダラと過ごしてしまう。晩からTODOを行う。今日から朝にかけて強烈な台風が接近する。', 0, 0, 'manual', NULL, '2012-09-15 00:00:00', '2017-02-03 14:14:45'),
(327, 'diary', '2012-09-16', '2012-09-16 00:00:00', '午前中は、眠ったり、ゲームをしたりして過ごす。午後からTODOを行う。台風16号は最大風速75ｍの割にはそれほど強さは感じられず。外の被害状況を見なければ詳しいことは分からないのだが。', 0, 0, 'manual', NULL, '2012-09-16 00:00:00', '2017-02-03 14:14:45'),
(328, 'diary', '2012-09-17', '2012-09-17 00:00:00', '午前は王国会館を清掃する。午後は中部まで電子部品を買いに行く。しかし休日であったため、代わりに本を買う。', 0, 0, 'manual', NULL, '2012-09-17 00:00:00', '2017-02-03 14:14:45'),
(329, 'diary', '2012-09-18', '2012-09-18 00:00:00', '午前は少しTODOを行う。', 0, 0, 'manual', NULL, '2012-09-18 00:00:00', '2017-02-03 14:14:45'),
(330, 'diary', '2012-09-19', '2012-09-19 00:00:00', '午前はひたすらAndroidの勉強。午後も少し勉強。今帰仁の畑へ。夕方は疲れたのでゆっくり過ごす。', 0, 0, 'manual', NULL, '2012-09-19 00:00:00', '2017-02-03 14:14:45'),
(331, 'diary', '2012-09-20', '2012-09-20 00:00:00', '午前はひたすらAndroidの勉強。ティモが懐く。夕方は集会の予定。夜釣り計画を立てようと思う。', 0, 0, 'manual', NULL, '2012-09-20 00:00:00', '2017-02-03 14:14:45'),
(332, 'diary', '2012-09-21', '2012-09-21 00:00:00', '午前中奉仕。午後はいつのまにか時間が過ぎてしまっている。', 0, 0, 'manual', NULL, '2012-09-21 00:00:00', '2017-02-03 14:14:45'),
(333, 'diary', '2012-09-22', '2012-09-22 00:00:00', '午前中奉仕。午後はいつのまにか時間が過ぎてしまっている。', 0, 0, 'manual', NULL, '2012-09-22 00:00:00', '2017-02-03 14:14:45'),
(334, 'diary', '2012-09-23', '2012-09-23 00:00:00', '午前中奉仕。午後は畑。その後、ダラダラと過ごしてしまう。疲れたら寝るようにしたい。トウギョの生息地は億首川とウッパマにあるかもしれない池が有力。', 0, 0, 'manual', NULL, '2012-09-23 00:00:00', '2017-02-03 14:14:45'),
(335, 'diary', '2012-09-24', '2012-09-24 00:00:00', '午前中はしっかりとTODOを行う。勉強が遅れ気味。ノルマの勉強をやってから外出するようにする。強力な台風がまた、来ようとしている。今回は強そう。', 0, 0, 'manual', NULL, '2012-09-24 00:00:00', '2017-02-03 14:14:45'),
(336, 'diary', '2012-09-25', '2012-09-25 00:00:00', '朝は気力が低下していたが、水と茶を大量に飲み、エサやり等をすることで少しずつやる気を回復する。非常に気持ちの良い天候と温度である。午後はウッパマへ散歩しながらトウギョでも探しに行こうか思案中。結局、TODOをやりながら過ごす。', 0, 0, 'manual', NULL, '2012-09-25 00:00:00', '2017-02-03 14:14:45'),
(337, 'diary', '2012-09-26', '2012-09-26 00:00:00', '食べ過ぎのためか、朝は疲れている。午前は少しTODOを行う。一日中水草の商売について考える。なんか気持ちが焦ってしまう。午後はTODOを行う。', 0, 0, 'manual', NULL, '2012-09-26 00:00:00', '2017-02-03 14:14:45'),
(338, 'diary', '2012-09-27', '2012-09-27 00:00:00', '午前中は、Yahooニュースのせいで気を散らされてしまう。辛抱強さを示し、無心でTODOを行う必要がある。例の台風が接近中。月曜日あたりに来るだろうか。今度のはかなり強いかもしれない。', 0, 0, 'manual', NULL, '2012-09-27 00:00:00', '2017-02-03 14:14:45'),
(339, 'diary', '2012-09-28', '2012-09-28 00:00:00', '早起きしてTODOを行い、奉仕へ。午後はTODOを行い、じいさんの病院へ。ついでに畑の台風対策も行う。', 0, 0, 'manual', NULL, '2012-09-28 00:00:00', '2017-02-03 14:14:45'),
(340, 'diary', '2012-09-29', '2012-09-29 00:00:00', '本日、台風18号中。そこまで大きくない。しかし今日はなぜか疲れている。疲れにくい体作りをしたい。', 0, 0, 'manual', NULL, '2012-09-29 00:00:00', '2017-02-03 14:14:45'),
(341, 'diary', '2012-09-30', '2012-09-30 00:00:00', '午前は集会。午後は何をしたか覚えていない。', 0, 0, 'manual', NULL, '2012-09-30 00:00:00', '2017-02-03 14:14:45'),
(342, 'diary', '2012-10-01', '2012-10-01 00:00:00', '午前はＴＯＤＯ、午後は啓と羽地大川やウッパマへ行く。ウッパマでは尾びれの長いグッピーを目撃する。', 0, 0, 'manual', NULL, '2012-10-01 00:00:00', '2017-02-03 14:14:45'),
(343, 'diary', '2012-10-02', '2012-10-02 00:00:00', 'やることが多すぎて、非常に疲れてしまう。肉体的な疲れから精神的な疲れまで発動する。', 0, 0, 'manual', NULL, '2012-10-02 00:00:00', '2017-02-03 14:14:45'),
(344, 'diary', '2012-10-03', '2012-10-03 00:00:00', '疲れを癒すため、遅起きする。ひたすら勉強と掃除を同時に行う。ミクロソリウムがシダ病にかかる。水温が問題化と思われる。夕方は畑と買い物へ。', 0, 0, 'manual', NULL, '2012-10-03 00:00:00', '2017-02-03 14:14:45'),
(345, 'diary', '2012-10-04', '2012-10-04 00:00:00', '早起きしてTODOを行う。ゲームはTODOを少しやると良い。太り始めてしまう。3度の食事は取るが非常に少なくする。午後はＴＯＤＯを行う。夕方集会へ行くがあることが原因でかなり気落ちする。あることとは、馬超が補助開拓しろと強烈で激しい圧力をかけてきたこと。', 0, 0, 'manual', NULL, '2012-10-04 00:00:00', '2017-02-03 14:14:45'),
(346, 'diary', '2012-10-05', '2012-10-05 00:00:00', '昨日のことが原因でうつ状態。一日中寝て過ごす。気分が晴れない。何もできない。', 0, 0, 'manual', NULL, '2012-10-05 00:00:00', '2017-02-03 14:14:45'),
(347, 'diary', '2012-10-06', '2012-10-06 00:00:00', 'うつ状態が続くが、問題が解決され気分が晴れる。逆に高揚状態でＴＯＤＯがあまりできず。', 0, 0, 'manual', NULL, '2012-10-06 00:00:00', '2017-02-03 14:14:45'),
(348, 'diary', '2012-10-07', '2012-10-07 00:00:00', '朝は集会。集会後、招待公演に来た兄弟をもてなすための食事会を執り行う。午後は無駄に時間を過ごした後、畑へ。晩からＴＯＤＯを開始。いつもどおりの生活に戻ってきただろうか。', 0, 0, 'manual', NULL, '2012-10-07 00:00:00', '2017-02-03 14:14:45'),
(349, 'diary', '2012-10-11', '2012-10-11 00:00:00', '午前中の遅くに、おじいさんの心臓が弱まったとの連絡が病院から来る。急いで駆けつけてみたが、すでに息はなかった。医師による死亡確認が行われた。12時１７分が死亡時刻。眠るように死んだとのことだが、前日から痰を取っても取っても取りきれなかったと看護士が言っていた。実際はどうだったのだろうか。すぐに葬式手配や親族への連絡を行い、真を空港まで迎えに行く。非常に忙しい一日となる。裕介も来たので久々に一家勢ぞろいとなる。', 0, 0, 'manual', NULL, '2012-10-11 00:00:00', '2017-02-03 14:14:45'),
(350, 'diary', '2012-10-12', '2012-10-12 00:00:00', '朝から、死亡届を役場に出し、火葬許可証をもらう。ついでに乙羽園にも連絡する。病院から遺体を業者とともに運び出し、今帰仁の家に配置する。そのとき祭壇も築かれる。親族もやってくる。裕介、真は叔母を迎えに空港へ、啓は部屋掃除。晩は遺体の前で飲み食いする。夜遅くになりオバーが大暴れし、非常に疲れる。遺体のそばで寝ていたが、オバーのほうが気がかりであった。', 0, 0, 'manual', NULL, '2012-10-12 00:00:00', '2017-02-03 14:14:45'),
(351, 'diary', '2012-10-13', '2012-10-13 00:00:00', '午前中に談笑のみの葬式を行い、火葬場へ遺体を運ぶ。火葬は3時間におよんだ。骨を拾うときは複雑な気分だった。骨があんなにもろいとは思わなかった。その後、墓へ移動し、墓を開き、骨を入れる。その際、先祖調査を行う。立派な骨壷が１つ。やや立派な骨壷が２つ。質素な骨壷が２つ。小さい骨壷が１つあった。推定ではあるが、幸八郎、幸八郎の妻、若くして死んだ幸四郎の娘ナベ、幸二郎、幸四郎、たま、であるかと思われる。養子の家だとか、女系がつづいたということから、幸八郎、幸四郎は養子であった可能性がある。吉田家への拘りと、養子家系を気にしていたことが、幸八郎や、旧上原家のなぞにつながったかと思われる。（後日：養子は幸八郎だけらしい）', 0, 0, 'manual', NULL, '2012-10-13 00:00:00', '2017-02-03 14:14:45'),
(352, 'diary', '2012-10-14', '2012-10-14 00:00:00', '午前中は自分ひとりだけで集会へ行く。午後は叔母らを空港へ送り届けに行く。', 0, 0, 'manual', NULL, '2012-10-14 00:00:00', '2017-02-03 14:14:45'),
(353, 'diary', '2012-10-15', '2012-10-15 00:00:00', '午前中は疲れで半分眠った状態。午後は畑で台風対策をする。', 0, 0, 'manual', NULL, '2012-10-15 00:00:00', '2017-02-03 14:14:45'),
(354, 'diary', '2012-10-16', '2012-10-16 00:00:00', '午前中はＴＯＤＯを行う。今度の台風は南側でウロチョロしてなかなかやってこず。木曜日あたりくることになってはいるかどうなるだろうか。　おじいさんに関する用事はまだたくさんあるのですべてこなす。夕方になって台風情報を見るとだいぶ勢力が弱まっている。一応台風対策を行う。晩にカネシ兄弟が来て、雑談を楽しんだ。', 0, 0, 'manual', NULL, '2012-10-16 00:00:00', '2017-02-03 14:14:45'),
(355, 'diary', '2012-10-17', '2012-10-17 00:00:00', 'TODOを行う。一瞬で過ぎたかのように思える一日。', 0, 0, 'manual', NULL, '2012-10-17 00:00:00', '2017-02-03 14:14:45'),
(356, 'diary', '2012-10-18', '2012-10-18 00:00:00', '台風それた。', 0, 0, 'manual', NULL, '2012-10-18 00:00:00', '2017-02-03 14:14:45'),
(357, 'diary', '2012-10-19', '2012-10-19 00:00:00', '午前中は、奉仕に。午後は畑。朝が寒く風邪をひきかける。', 0, 0, 'manual', NULL, '2012-10-19 00:00:00', '2017-02-03 14:14:45'),
(358, 'diary', '2012-10-20', '2012-10-20 00:00:00', 'ＴＯＤＯを行う。仕事のことで心が乱されるが、もちなおす。', 0, 0, 'manual', NULL, '2012-10-20 00:00:00', '2017-02-03 14:14:45'),
(359, 'diary', '2012-10-21', '2012-10-21 00:00:00', '朝から風邪気味。午前中集会。午後は休養。晩からニト家に食事招待に行く。ニト、カネシ、トウヤマ兄弟らと楽しく過ごす。', 0, 0, 'manual', NULL, '2012-10-21 00:00:00', '2017-02-03 14:14:45'),
(360, 'diary', '2012-10-22', '2012-10-22 00:00:00', '朝からＴＯＤＯを行う。午後はハローワークへ行き、母の買い物にも付き合う。', 0, 0, 'manual', NULL, '2012-10-22 00:00:00', '2017-02-03 14:14:45'),
(361, 'diary', '2012-10-23', '2012-10-23 00:00:00', '朝からＴＯＤＯを行う。午後もＴＯＤＯを行う。', 0, 0, 'manual', NULL, '2012-10-23 00:00:00', '2017-02-03 14:14:45'),
(362, 'diary', '2012-10-24', '2012-10-24 00:00:00', '午前中は奉仕活動にでる。午後は気晴らしに海洋博記念公園を散歩する。', 0, 0, 'manual', NULL, '2012-10-24 00:00:00', '2017-02-03 14:14:45'),
(363, 'diary', '2012-10-25', '2012-10-25 00:00:00', '午前中はＴＯＤＯ。午後はおじいさんに関する用事。仕事をモット速く進める必要がある。時間指定と残り日数表示方法を活用する。', 0, 0, 'manual', NULL, '2012-10-25 00:00:00', '2017-02-03 14:14:45'),
(364, 'diary', '2012-10-26', '2012-10-26 00:00:00', '午前中は奉仕へ', 0, 0, 'manual', NULL, '2012-10-26 00:00:00', '2017-02-03 14:14:45'),
(365, 'diary', '2012-10-27', '2012-10-27 00:00:00', '覚えておらず', 0, 0, 'manual', NULL, '2012-10-27 00:00:00', '2017-02-03 14:14:45'),
(366, 'diary', '2012-10-28', '2012-10-28 00:00:00', '午前中は奉仕へ', 0, 0, 'manual', NULL, '2012-10-28 00:00:00', '2017-02-03 14:14:45'),
(367, 'diary', '2012-10-29', '2012-10-29 00:00:00', '用事で外出', 0, 0, 'manual', NULL, '2012-10-29 00:00:00', '2017-02-03 14:14:45'),
(368, 'diary', '2012-10-30', '2012-10-30 00:00:00', '覚えておらず', 0, 0, 'manual', NULL, '2012-10-30 00:00:00', '2017-02-03 14:14:45'),
(369, 'diary', '2012-10-31', '2012-10-31 00:00:00', '覚えておらず', 0, 0, 'manual', NULL, '2012-10-31 00:00:00', '2017-02-03 14:14:45'),
(370, 'diary', '2012-11-01', '2012-11-01 00:00:00', '少し風邪をひく？　体調はあまりよくないのでとりあえず一日中や住む。ＴＯＤＯをやりつつ休んだほうが良かった。', 0, 0, 'manual', NULL, '2012-11-01 00:00:00', '2017-02-03 14:14:45'),
(371, 'diary', '2012-11-02', '2012-11-02 00:00:00', '霊的な回復のためＴＯＤＯを行う。午後はハローワークへ', 0, 0, 'manual', NULL, '2012-11-02 00:00:00', '2017-02-03 14:14:45'),
(372, 'diary', '2012-11-03', '2012-11-03 00:00:00', '午前中奉仕へ行く。午後は池を作成', 0, 0, 'manual', NULL, '2012-11-03 00:00:00', '2017-02-03 14:14:45'),
(373, 'diary', '2012-11-04', '2012-11-04 00:00:00', '午前中は奉仕。午後は昨日の疲れから長めに寝てしまう。晩にＴＯＤＯを行う。', 0, 0, 'manual', NULL, '2012-11-04 00:00:00', '2017-02-03 14:14:45'),
(374, 'diary', '2012-11-05', '2012-11-05 00:00:00', '午前中は調子が悪く何もできず。午後は母の用事のあと、畑へ行く。何かとあせってしまう。時間が早く過ぎる上に疲れもある。どうすればよいのか。ゲームはあくまで、仕事を行う上でのサポートであることをよく認識せねばならず。', 0, 0, 'manual', NULL, '2012-11-05 00:00:00', '2017-02-03 14:14:45'),
(375, 'diary', '2012-11-06', '2012-11-06 00:00:00', '巡回訪問の週。夕方は集会。注解の仕方を励まされる。', 0, 0, 'manual', NULL, '2012-11-06 00:00:00', '2017-02-03 14:14:45'),
(376, 'diary', '2012-11-07', '2012-11-07 00:00:00', '覚えておらず', 0, 0, 'manual', NULL, '2012-11-07 00:00:00', '2017-02-03 14:14:45'),
(377, 'diary', '2012-11-08', '2012-11-08 00:00:00', '夕方は集会。畑で池作りなども行ったが、有意義な一日とは言えず。', 0, 0, 'manual', NULL, '2012-11-08 00:00:00', '2017-02-03 14:14:45'),
(378, 'diary', '2012-11-09', '2012-11-09 00:00:00', '覚えておらず。仕事の進み具合で思いあぐねているのは確か。', 0, 0, 'manual', NULL, '2012-11-09 00:00:00', '2017-02-03 14:14:45'),
(379, 'diary', '2012-11-10', '2012-11-10 00:00:00', '調子が悪い。TODOをちゃんとこなしていないのが原因。', 0, 0, 'manual', NULL, '2012-11-10 00:00:00', '2017-02-03 14:14:45'),
(380, 'diary', '2012-11-11', '2012-11-11 00:00:00', '午前中は集会。非常に調子が悪い。奉仕に出かけるが遅刻と気力低下により引き返す。。結局ダラダラと過ごす。', 0, 0, 'manual', NULL, '2012-11-11 00:00:00', '2017-02-03 14:14:45'),
(381, 'diary', '2012-11-12', '2012-11-12 00:00:00', '午前中はぼんやりしているうちに時間が過ぎてしまう。午後はAndroidの勉強をしたあと、畑と買い物へ。やるべきことを列挙してから１日をはじめる。', 0, 0, 'manual', NULL, '2012-11-12 00:00:00', '2017-02-03 14:14:45'),
(382, 'diary', '2012-11-13', '2012-11-13 00:00:00', 'またダイエットしないとやばい。また太り始めている。食欲を抑えるため野菜のみの食事とする。ダラダラとインターネットをする習慣を避ける。', 0, 0, 'manual', NULL, '2012-11-13 00:00:00', '2017-02-03 14:14:45'),
(383, 'diary', '2012-11-14', '2012-11-14 00:00:00', '結局、別のものを食べてしまった。わずかなご飯で我慢する習慣を身に着けなければならない。', 0, 0, 'manual', NULL, '2012-11-14 00:00:00', '2017-02-03 14:14:45'),
(384, 'diary', '2012-11-15', '2012-11-15 00:00:00', '午前中及び午後は何をしていたか覚えていないが、有意義に時間を過ごしていないことは確か。夕方は集会。', 0, 0, 'manual', NULL, '2012-11-15 00:00:00', '2017-02-03 14:14:45'),
(385, 'diary', '2012-11-16', '2012-11-16 00:00:00', '午前中遅くに奉仕へ行く。午後は源河川へ行く。', 0, 0, 'manual', NULL, '2012-11-16 00:00:00', '2017-02-03 14:14:45'),
(386, 'diary', '2012-11-17', '2012-11-17 00:00:00', 'ボーとしているうちに午前中が過ぎる。午後は、名護へ。奉仕へ行く予定だったが雨で中止する。', 0, 0, 'manual', NULL, '2012-11-17 00:00:00', '2017-02-03 14:14:45'),
(387, 'diary', '2012-11-18', '2012-11-18 00:00:00', '午前中は集会。午後はDVDを見るなど無駄に時間を過ごしてしまう。疲れたときは横になって寝るようにしよう。もちろんアラームタイマーを忘れないように。', 0, 0, 'manual', NULL, '2012-11-18 00:00:00', '2017-02-03 14:14:45'),
(388, 'diary', '2012-11-19', '2012-11-19 00:00:00', '仕事の進め方について考察しならが、海洋博記念公園を散歩する。美ら海水族館へ行く。タナゴモドキを見ることができたのは良かった。なんとタマゴは0.3mmらしい。エサを工夫したとのことだが、インフゾリアでなんとかなるかも。タナゴモドキを入手する方法はないものか。', 0, 0, 'manual', NULL, '2012-11-19 00:00:00', '2017-02-03 14:14:45'),
(389, 'diary', '2012-11-20', '2012-11-20 00:00:00', '午前中は何をしたか覚えておらず。夕方から母と今帰仁へ。しかし母の大掃除の結果、オバーの徘徊を引き起こすことになったと思う。', 0, 0, 'manual', NULL, '2012-11-20 00:00:00', '2017-02-03 14:14:45'),
(390, 'diary', '2012-11-21', '2012-11-21 00:00:00', '午前中は奉仕に行く。午後は今帰仁へ。TODOでできていない点を反省。TODOをやってから畑に行くべきだろう。あと、オバーが深夜徘徊を行い、警察に捕まる。', 0, 0, 'manual', NULL, '2012-11-21 00:00:00', '2017-02-03 14:14:45'),
(391, 'diary', '2012-11-22', '2012-11-22 00:00:00', '本日は雨。Androidの勉強など。TODOを行う。雨の日はなぜか調子が悪い。', 0, 0, 'manual', NULL, '2012-11-22 00:00:00', '2017-02-03 14:14:45'),
(392, 'diary', '2013-01-01', '2013-01-01 00:00:00', '１２月中はひたすらゲーム開発を行っており、TODOがほとんどできていない。仕事に対するあせりを感じている。正月は裕介が来た。', 0, 0, 'manual', NULL, '2013-01-01 00:00:00', '2017-02-03 14:14:45'),
(393, 'diary', '2013-01-02', '2013-01-02 00:00:00', '今年の目標は。「人に関心を持つこと」そのためには基本的な人間の質問をよく調査し話せるようにすることだ。他の人に話しをしたいと思うくらいに徹底的に調査しなければならない。これができれば、再訪問ができるようになるかもしれない。', 0, 0, 'manual', NULL, '2013-01-02 00:00:00', '2017-02-03 14:14:45'),
(394, 'diary', '2013-01-04', '2013-01-04 00:00:00', 'オオジャナのおじいさんが一時的に危篤状態になる。急遽おじいさんのところへ。でもとりあえず大丈夫だった。', 0, 0, 'manual', NULL, '2013-01-04 00:00:00', '2017-02-03 14:14:45'),
(395, 'diary', '2013-01-08', '2013-01-08 00:00:00', 'しばらく、日誌をサボっていたが本日から開始。今年も仕事と奉仕と霊的な面をがんばらねば。', 0, 0, 'manual', NULL, '2013-01-08 00:00:00', '2017-02-03 14:14:45'),
(396, 'diary', '2013-01-11', '2013-01-11 00:00:00', '昨日、仕事の件でひどく悩み、強く祈る。本日、奉仕でる。なんと仕事依頼のメールが来た。エホバは生きておられる。', 0, 0, 'manual', NULL, '2013-01-11 00:00:00', '2017-02-03 14:14:45'),
(397, 'diary', '2013-01-15', '2013-01-15 00:00:00', 'おじいさんの病状はかなり悪い。癌で骨折までしている。', 0, 0, 'manual', NULL, '2013-01-15 00:00:00', '2017-02-03 14:14:45'),
(398, 'diary', '2013-01-17', '2013-01-17 00:00:00', 'ゲーム製作を再考察。現在作っているゲームは良心の呵責を感じるため、別のゲームを作ることにする。', 0, 0, 'manual', NULL, '2013-01-17 00:00:00', '2017-02-03 14:14:45'),
(399, 'diary', '2013-01-18', '2013-01-18 00:00:00', '午前中は奉仕に行く。莫大な健康保険税にイラだつ。', 0, 0, 'manual', NULL, '2013-01-18 00:00:00', '2017-02-03 14:14:45'),
(400, 'diary', '2013-01-19', '2013-01-19 00:00:00', '本日、オオジャナのおじいさんのところへ行く予定。', 0, 0, 'manual', NULL, '2013-01-19 00:00:00', '2017-02-03 14:14:45'),
(401, 'diary', '2013-01-20', '2013-01-20 00:00:00', '午前中は集会へ。', 0, 0, 'manual', NULL, '2013-01-20 00:00:00', '2017-02-03 14:14:45'),
(402, 'diary', '2013-01-21', '2013-01-21 00:00:00', 'ゲーム開発をする。他に何をしたか覚えていないが有意義ではない一日のような気がする。', 0, 0, 'manual', NULL, '2013-01-21 00:00:00', '2017-02-03 14:14:45'),
(403, 'diary', '2013-01-22', '2013-01-22 00:00:00', 'おじいさんのところへ。おじいさんが字を書く。「来てくれてありがとう」などと書いていたと思われる。', 0, 0, 'manual', NULL, '2013-01-22 00:00:00', '2017-02-03 14:14:45'),
(404, 'diary', '2013-01-23', '2013-01-23 00:00:00', '午前は仕事。そして夕方の奉仕へ出る。おじいさんが水も飲めない状態で、点滴をはずしたとのこと（自然死の準備）。おじいさんがまもなく死ぬことが決定的になり動揺する。', 0, 0, 'manual', NULL, '2013-01-23 00:00:00', '2017-02-03 14:14:45'),
(405, 'diary', '2013-01-24', '2013-01-24 00:00:00', '本日仕事の打ち合わせ。ゆいレールにはじめて乗り、内地の電車感覚を久しぶりに味わう。真がおじいさんに会いに来る。日帰りである。裕介も合流し、３人でおじいさんを見舞いする。夏子叔母さんと良二叔父さんがいた。おじいさんを死ぬ前に見る最後の姿である。返事も少しはできるようだ。', 0, 0, 'manual', NULL, '2013-01-24 00:00:00', '2017-02-03 14:14:45'),
(406, 'diary', '2013-01-25', '2013-01-25 00:00:00', '午前中は奉仕にでる。父母がおじいさんのところへ行く。馬超は泊り込み。すでに反応は薄いとのこと', 0, 0, 'manual', NULL, '2013-01-25 00:00:00', '2017-02-03 14:14:45'),
(407, 'diary', '2013-01-26', '2013-01-26 00:00:00', '午前中は仕事。午後はナキジンへ畑をする。', 0, 0, 'manual', NULL, '2013-01-26 00:00:00', '2017-02-03 14:14:45'),
(408, 'diary', '2013-01-27', '2013-01-27 00:00:00', 'オオジャナのおじいさん、ついに亡くなる。21：45分', 0, 0, 'manual', NULL, '2013-01-27 00:00:00', '2017-02-03 14:14:45'),
(409, 'diary', '2013-01-28', '2013-01-28 00:00:00', 'オオジャナへ行き通夜。おじいいさんはすでに棺おけに。ふたをしていないのが良い。おじいさんの日誌を見、自分も日誌を書かなければと励まされる。最後のお爺さんのしごとはバジル植えだったらしい。最後は意識がない状態で、大きな息を一つ吐き、しばらくしてから心臓がとまって死亡とのこと。久しぶりにタツノリを見、そして奥さんのサエさんに会う。ジョー、マナト、レミは暴れながら楽しそうに遊んでいた。ジョーは掛算まで覚えるようになっている。子供の成長は早い。', 0, 0, 'manual', NULL, '2013-01-28 00:00:00', '2017-02-03 14:14:45'),
(410, 'diary', '2013-01-29', '2013-01-29 00:00:00', 'わずかな期間で二人のおじいさんが死んだわけだが、どちらの死がよかったのだろうか。ナキジンのおじいさんの最後は認知症と肺炎、痰との闘いだった。いつ死ぬかわからない状態で急に死んだ。最後まで痰を取り続けていた。オオジャナのおじいさんは眠るように死んだが、認知症ではないため、もう少し点滴で生きられたのではないかと少し思う。でも限界は医者でないと分からないので、この判断が正しかったのだろう。個人的にはオオジャナのおじいさんの自然死がよいかと思われる。明日葬式。本日、タブレットを購入予定。', 0, 0, 'manual', NULL, '2013-01-29 00:00:00', '2017-02-03 14:14:45'),
(411, 'diary', '2013-02-19', '2013-02-19 00:00:00', '自信を失ったとき。積極的なことで頭をいっぱいにする。小さな目標を立て、それを達成する。', 0, 0, 'manual', NULL, '2013-02-19 00:00:00', '2017-02-03 14:14:45'),
(412, 'diary', '2013-03-30', '2013-03-30 00:00:00', 'ここしばらく、仕事の忙しさにかまけ、毎日の行うことがらがないがしろになってしまった。本日から日誌をつけよう。仕事の納期が遅れてしまい、先日、チーム全体で怒られた。かなりへこんだ。よくよく記録を見てみると、15,18日には予定通りお客さんに見せるべきものはできていた。その後、AppMobiはiOS版で動かないことが発覚。事前にそのことを良く知っていらば良かった。プロジェクトrff5をひそかに実用的なものにしておいておこうか。', 0, 0, 'manual', NULL, '2013-03-30 00:00:00', '2017-02-03 14:14:45'),
(413, 'diary', '2013-03-31', '2013-03-31 00:00:00', '集会へ。牧羊訪問を受ける。聞いたことを守ること。主の業に携われば、他のものも与えられること。とりあえず配布雑誌の文言を考えよう。　語るための個人研究もしなくては。　集会から帰ると啓が水疱瘡になっていた。', 0, 0, 'manual', NULL, '2013-03-31 00:00:00', '2017-02-03 14:14:45'),
(414, 'diary', '2013-04-01', '2013-04-01 00:00:00', '啓が水疱瘡で苦しむ。明日は看護学校のオリエンテーションである。どうすれば良いだろうか？本日、エノ兄弟のボーリング大会に行く。できるだけ重いボールを使うと調子が良い。重いボールを持って、調子の良いフォームを感覚で覚え、中間目印線を狙って投げる。　仕事はあいかわらず忙しいが、今のところ順調そうである。祈りの後は、問題がよく解決される。啓のオリエンテーションもなんとかなることを願うばかり。', 0, 0, 'manual', NULL, '2013-04-01 00:00:00', '2017-02-03 14:14:45'),
(415, 'diary', '2013-04-13', '2013-04-13 00:00:00', '仕事のいそがしさで奉仕へいけず。明日は何とか行かないと。ニト兄弟のプリンタがＰＣに接続できない。初期不良かどうか調べる必要がある。初期不良なら返品手続き。初期不良でないならWindows7で実装方法を確かめて、もう一度、ニト兄弟のところへ行って接続しよう。', 0, 0, 'manual', NULL, '2013-04-13 00:00:00', '2017-02-03 14:14:45'),
(416, 'diary', '2013-04-14', '2013-04-14 00:00:00', '午前中はいつもの通り集会へ。昨日のプリンタだが、家のＰＣで試すと動いた。ネットワーク接続がヒントのようだ。後日ニト兄弟のＰＣでも試す。本日はずっと雨。奉仕には行けず。', 0, 0, 'manual', NULL, '2013-04-14 00:00:00', '2017-02-03 14:14:45'),
(417, 'diary', '2013-04-15', '2013-04-15 00:00:00', '仕事の報酬のことで悩む。', 0, 0, 'manual', NULL, '2013-04-15 00:00:00', '2017-02-03 14:14:45'),
(418, 'diary', '2013-04-16', '2013-04-16 00:00:00', '仕事の報酬のことと今後のことで悩む。このままもらえなくなる可能性も高い。とりあえず5月いっぱいまでは体力を鍛えながら、ゲーム開発を行い様子を見る。内地へ短期派遣に行くことも考える必要がある。', 0, 0, 'manual', NULL, '2013-04-16 00:00:00', '2017-02-03 14:14:45'),
(419, 'diary', '2013-04-17', '2013-04-17 00:00:00', '朝から腹の調子が悪い。なんとか奉仕にいけないものか。気持ちを安定させられれば・・・。 午後は奉仕にでることができた。', 0, 0, 'manual', NULL, '2013-04-17 00:00:00', '2017-02-03 14:14:45'),
(420, 'diary', '2013-04-18', '2013-04-18 00:00:00', 'とりあえず、Androidゲーム開発を中心に行い、体力強化トレーニングも行うことにする。東京へ行く場合、金銭面で不安あり。農作業スタッフになるばあい、仕事があるか不安である。東京へ行く場合、お金を借りよう。体力があればできるバイトも増える。一度、職安で短期バイトを探してみる。', 0, 0, 'manual', NULL, '2013-04-18 00:00:00', '2017-02-03 14:14:45'),
(421, 'diary', '2013-04-30', '2013-04-30 00:00:00', 'サビラさんから仕事の紹介が来る。浦添市役所へ行く。すぐに仕事の引継ぎ作業を行う。', 0, 0, 'manual', NULL, '2013-04-30 00:00:00', '2017-02-03 14:14:45'),
(422, 'diary', '2013-05-01', '2013-05-01 00:00:00', '午後から出勤。仕事に早速取り掛かる。難しい。コーヒーの飲みすぎもあり、夜寝れない。結局2時間程度の睡眠。', 0, 0, 'manual', NULL, '2013-05-01 00:00:00', '2017-02-03 14:14:45'),
(423, 'diary', '2013-05-02', '2013-05-02 00:00:00', '思った以上にはるかに難しい。この仕事から逃げようかと思うほど。引き受けるのではなかったと激しく後悔する。自分で開発するアプリが一番良い。集会へもいけない。終わりあたりで少し光明を見出した状態で仕事を終えれたのが良かった。でなければ逃げていたかもしれない。', 0, 0, 'manual', NULL, '2013-05-02 00:00:00', '2017-02-03 14:14:45'),
(424, 'diary', '2013-05-03', '2013-05-03 00:00:00', '休み。疲れでひたすら寝る。', 0, 0, 'manual', NULL, '2013-05-03 00:00:00', '2017-02-03 14:14:45'),
(425, 'diary', '2013-05-04', '2013-05-04 00:00:00', '難関部分を何とか解析し、設計図を作れるところまでやる。ビジネスホテルに泊まる。ちょっと気分が良い。サンエーもあり買い物に不便はなし。ホテルも宇宙船内部のようなかんじで良い。', 0, 0, 'manual', NULL, '2013-05-04 00:00:00', '2017-02-03 14:14:45'),
(426, 'diary', '2013-05-05', '2013-05-05 00:00:00', '難関部分であるバッチ処理のコーティングをほぼ完了。しかしまだ画面から動かせず。ビジネスホテルに泊まる。', 0, 0, 'manual', NULL, '2013-05-05 00:00:00', '2017-02-03 14:14:45'),
(427, 'diary', '2013-05-06', '2013-05-06 00:00:00', '半日でやっとバッチが動かせるようになる。次の作業の仕様解析を行う。これも難しい。ドーパミンとセレトニンを出す方法について調べる。別のビジネスホテルに泊まる。前より古いが広い。出入りも自由である。うるさいのが難点か。', 0, 0, 'manual', NULL, '2013-05-06 00:00:00', '2017-02-03 14:14:45'),
(428, 'diary', '2013-06-21', '2013-06-21 00:00:00', 'やっと浦添役所の仕事を終える。集会へいけるような仕事を取らねばならず', 0, 0, 'manual', NULL, '2013-06-21 00:00:00', '2017-02-03 14:14:45'),
(429, 'diary', '2013-07-05', '2013-07-05 00:00:00', '奉仕へ行こうとするが、道の間違いと、時間遅刻により断念。午後は無駄に時間を過ごしてしまう。合鍵やYシャツを買いに名護へ。車を洗わないと汚すぎる。夕方からTODO', 0, 0, 'manual', NULL, '2013-07-05 00:00:00', '2017-02-03 14:14:45'),
(430, 'diary', '2013-07-06', '2013-07-06 00:00:00', '午前中は奉仕に出る。', 0, 0, 'manual', NULL, '2013-07-06 00:00:00', '2017-02-03 14:14:45'),
(431, 'diary', '2013-07-07', '2013-07-07 00:00:00', '集会へ。午後はゆっくりテレビを見る。', 0, 0, 'manual', NULL, '2013-07-07 00:00:00', '2017-02-03 14:14:45'),
(432, 'diary', '2013-07-08', '2013-07-08 00:00:00', '午前中開発。午後は気力をなくし、気づけば夕方になっていた。無駄にインターネットで時間を過ごしてしまう', 0, 0, 'manual', NULL, '2013-07-08 00:00:00', '2017-02-03 14:14:45'),
(433, 'diary', '2013-07-09', '2013-07-09 00:00:00', '午前中は開発作業。午後もだらだら開発。オバーが先日、近所の人の敷地内でゴミを捨てていたので、オバーも連れて、謝罪とゴミ回収に行く。オバーが暴れて大変疲れた。', 0, 0, 'manual', NULL, '2013-07-09 00:00:00', '2017-02-03 14:14:45'),
(434, 'diary', '2013-07-10', '2013-07-10 00:00:00', '午前中は開発作業。遅くおきてしまい、ダラダラと時間をすごすこともあった。外出しながら、開発できないか模索したいところ。他の作業をしながら開発するとやる気が出る。', 0, 0, 'manual', NULL, '2013-07-10 00:00:00', '2017-02-03 14:14:45'),
(435, 'diary', '2013-07-11', '2013-07-11 00:00:00', 'ゲーム開発と集会。　いろいろあり疲れ始める。', 0, 0, 'manual', NULL, '2013-07-11 00:00:00', '2017-02-03 14:14:45'),
(436, 'diary', '2013-07-12', '2013-07-12 00:00:00', 'ゲーム開発をする。', 0, 0, 'manual', NULL, '2013-07-12 00:00:00', '2017-02-03 14:14:45'),
(437, 'diary', '2013-07-13', '2013-07-13 00:00:00', '花火大会があった。いろいろ疲れてしまい寝込む。', 0, 0, 'manual', NULL, '2013-07-13 00:00:00', '2017-02-03 14:14:45'),
(438, 'diary', '2013-07-14', '2013-07-14 00:00:00', '集会を休む。いろいろ疲れている。　夕方散歩へ。エホバは祈りを聞いてくださり励ましてくださる。', 0, 0, 'manual', NULL, '2013-07-14 00:00:00', '2017-02-03 14:14:45'),
(439, 'diary', '2013-07-26', '2013-07-26 00:00:00', '暑すぎて扇風機を強にしたが、朝起きると体調が悪い。顔面神経痛もする。一日中やる気がおこらずボーと過ごす。個人研究として、さまざまな宗教について調べる。', 0, 0, 'manual', NULL, '2013-07-26 00:00:00', '2017-02-03 14:14:45'),
(440, 'diary', '2013-07-27', '2013-07-27 00:00:00', '風が吹いて涼しかったため寝起きが良い。午前中は奉仕にでる。午後も勉強と仕事に励む。', 0, 0, 'manual', NULL, '2013-07-27 00:00:00', '2017-02-03 14:14:45'),
(441, 'diary', '2013-07-29', '2013-07-29 00:00:00', '宜野湾へ行く。中城城に行く。', 0, 0, 'manual', NULL, '2013-07-29 00:00:00', '2017-02-03 14:14:45'),
(442, 'diary', '2013-07-30', '2013-07-30 00:00:00', '暑い。仕事に集中しづらい。しかしやらねば。', 0, 0, 'manual', NULL, '2013-07-30 00:00:00', '2017-02-03 14:14:45'),
(443, 'diary', '2013-08-02', '2013-08-02 00:00:00', '午前中は奉仕。午後は仕事準備。夕方は今帰仁の池に水を満たしに行く。', 0, 0, 'manual', NULL, '2013-08-02 00:00:00', '2017-02-03 14:14:45'),
(444, 'diary', '2013-08-03', '2013-08-03 00:00:00', '午前中は仕事。午後から啓と源河川へ行く。明日のクワガタ採取のための仕掛けをするため。ついでに川を漁る。めずらしい沖縄のドジョウとリュウキュウクロメダカと思われる魚を捕獲。非常に有益だった。', 0, 0, 'manual', NULL, '2013-08-03 00:00:00', '2017-02-03 14:14:45'),
(445, 'diary', '2013-08-04', '2013-08-04 00:00:00', '午前集会。午後は父と家修復。夕方は奉仕。夜からのぞみ兄弟、啓とクワガタ取り。外灯に雑魚虫ですら集まっていない。沖縄から昆虫は消えうせたのだろうか？', 0, 0, 'manual', NULL, '2013-08-04 00:00:00', '2017-02-03 14:14:45'),
(446, 'diary', '2013-08-05', '2013-08-05 00:00:00', '昨日の疲れがひどし', 0, 0, 'manual', NULL, '2013-08-05 00:00:00', '2017-02-03 14:14:45'),
(447, 'diary', '2013-08-06', '2013-08-06 00:00:00', '何をしたか覚えていない', 0, 0, 'manual', NULL, '2013-08-06 00:00:00', '2017-02-03 14:14:45'),
